import React, { useEffect, useState } from 'react';
import Navbar from '../navSection/Navbar';
import Container from 'react-bootstrap/Container';
import Button from 'react-bootstrap/Button';
import Card from 'react-bootstrap/Card';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import ListGroup from 'react-bootstrap/ListGroup';
import './order.css';
import OrderDetails from './OrderDetails';
import axios from 'axios';
import { useParams } from 'react-router-dom';

function Order() {
  // const data = {
  //   products: [
  //     {
  //       name: 'fried chicken',
  //       quantity: 5,
  //       category: 'meat',
  //       img: '/images/ghanajolof.jpg',
  //     },
  //     {
  //       name: 'bottle water',
  //       quantity: 1,
  //       category: 'water',
  //       img: '/images/freshfoods.jpg',
  //     },
  //     {
  //       name: 'jollof rice',
  //       quantity: 2,
  //       category: 'food',
  //       img: '/images/jolofffff.jpg',
  //     },
  //     {
  //       name: 'okro and panded yam',
  //       quantity: 7,
  //       category: 'food',
  //       img: '/images/okroandfufu.jpeg',
  //     },
  //   ],
  //   resident: {
  //     name: 'johnson paul',
  //     phone: '08037262827',
  //     address: '123 Main St, Suite 330, Boston MA, USA',
  //     km: 25,
  //   },
  //   total: 12000,
  // };
  const params = useParams();
  const { id } = params;
  const [data, setData] = useState({});

  useEffect(() => {
    const handleOrder = async () => {
      try {
        const { data } = await axios.get(
          `http://localhost:5000/api/orders/find/${id}`
        );
        console.log('singleOrder: ', data);
        setData(data);
      } catch (error) {
        console.log(error);
      }
    };
    handleOrder();
  }, [id]);

  const [smallScreen, setSmallScreen] = useState(false);
  useEffect(() => {
    const smallSize = () => {
      setSmallScreen(window.innerWidth < 1200);
    };
    smallSize();
    window.addEventListener('resize', smallSize);

    return () => {
      window.removeEventListener('resize', smallSize);
    };
  }, []);

  const items = (data) => {
    return data?.products?.map((item, index) => (
      <span key={index} className="font1 ">
        {item.category === 'food'
          ? `${item.quantity} plate  of ${item.name},`
          : item.category === 'meat '
          ? ` ${item.quantity} pieces of ${item.name},`
          : `${item.quantity} pieces of ${item.name},
                                `}
      </span>
    ));
  };

  return (
    <div>
      <Navbar />
      <Container className="my-3">
        <div className="fw-bold fs-5 text-center text-success">
          Order Details({data?.products?.length} items)
        </div>
        <div className="d-flex  p-3 border   justify-content-between ">
          <div className="d-flex flex-column ">
            <div className="">
              <h3 className='d-none d-md-flex'>Order ID: {data?.details?._id}</h3>
              <h3 className='d-m'>Order ID: {data?.details?._id}</h3>
            </div>
            <div className="d-flex flex-wrap  flex-column text-capitalize">
              <div className="d-flex flex-wrap  gap-2">
                <strong>Name: </strong>
                <span className="font">
                  {data?.details?.shippingAddress?.name}
                </span>
              </div>
              <div className="d-flex  gap-2">
                <strong>phone number: </strong>
                <span className="font">
                  {data.details?.shippingAddress?.phoneNumber}
                </span>
              </div>
              <div className="d-flex flex-wrap  gap-1">
                <strong>Address:</strong>
                <div>
                  {' '}
                  <span className="font">
                    {data.details?.shippingAddress?.street} {data.details?.shippingAddress?.localGvt}, {data.details?.shippingAddress?.state}{' '}{data.details?.shippingAddress?.country}
                  </span>
                </div>
              </div>
            </div>
            <div className="d-flex align-items-center mt-2 gap-3">
              <Button
                variant="success"
                className="fw-bold d-flex align-items-center"
              >
                Accept
              </Button>
              <Button
                variant="danger"
                className="fw-bold d-flex align-items-center"
              >
                Cancel
              </Button>
            </div>
          </div>
        </div>
        <div>
          <div className="fs-4 fw-bold text-center my-3">Items</div>
          <Row>
            <Col md={8} className='mb-3'>
              <OrderDetails data={data} smallScreen={smallScreen} />
            </Col>
            <Col md={4}>
              <ListGroup>
                <strong>Total:N{data?.details?.total}</strong>
                <ListGroup.Item className="font1bg">
                  {items(data)}
                </ListGroup.Item>
              </ListGroup>
            </Col>
          </Row>
        </div>
      </Container>
    </div>
  );
}

export default Order;
