import React from 'react';
import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';
import Navbar from '../navSection/Navbar';
import AccountBalanceIcon from '@mui/icons-material/AccountBalance';
import SearchBar from '../component/SearchBar';
import Masonry, {ResponsiveMasonry} from "react-responsive-masonry"
import './profile.css'
const Profile = () => {
  const data = [
    {
      contents: [
        { name: 'jollof rice', quantity: 2, category: 'food' },
        { name: 'fried chicken', quantity: 5, category: 'meat' },
        { name: 'bottle water', quantity: 1, category: 'water' },
        { name: 'okro and panded yam', quantity: 7, category: 'food' },
      ],
      img: '/images/ghanajolof.jpg',
    },
    {
      contents: [
        { name: 'jollof rice', quantity: 2, category: 'food' },
        { name: 'fried chicken', quantity: 5, category: 'meat' },
        { name: 'bottle water', quantity: 1, category: 'water' },
        { name: 'okro and panded yam', quantity: 7, category: 'food' },
      ],
      img: '/images/freshfoods.jpg',
    },
    {
      contents: [
        { name: 'jollof rice', quantity: 2, category: 'food' },
        { name: 'fried chicken', quantity: 5, category: 'meat' },
        { name: 'bottle water', quantity: 1, category: 'water' },
        { name: 'okro and panded yam', quantity: 7, category: 'food' },
      ],
      img: '/images/jolofffff.jpg',
    },
    {
      contents: [
        { name: 'jollof rice', quantity: 2, category: 'food' },
        { name: 'fried chicken', quantity: 5, category: 'meat' },
        { name: 'bottle water', quantity: 1, category: 'water' },
        { name: 'okro and panded yam', quantity: 7, category: 'food' },
      ],
      img: '/images/okroandfufu.jpeg',
    },
  ];

  return (
    <div>
      <Navbar />
      <Container className='d-flex flex-column gap-3'>
        <Row className="balance-info my-2">
          <Col className="d-flex p-3 border   justify-content-between ">
            <div className="d-flex flex-column ">
              {' '}
              <div className="d-flex flex-column mb-2">
                {' '}
                Available balance:
                <strong> N76,030.00</strong>
              </div>
              {/* Link or button for Sales History */}
              <div>
                {' '}
                <Button
                  variant="success"
                  className="fw-bold d-flex align-items-center"
                >
                  <AccountBalanceIcon />
                  CashOut
                </Button>
              </div>
            </div>
            <div className="text-end">
              {' '}
              <Button variant="link text-success">Sales History</Button>
            </div>
          </Col>
        </Row>

        <SearchBar />
        <ResponsiveMasonry
                columnsCountBreakPoints={{350: 1, 750: 2, 900: 2}}
            >
                <Masonry gutter='20px'>
        {data.map((item, index) => (
          <div key={index} className='d-flex align-items-center  gap-2 border rounded'>
            <div className="cardImage">
              <img src={item.img} alt="" />
            </div>
            {item.contents.map((content, index) => (
              <div key={index}>
                <span className='fw-bold'>
                  {content.category === 'food'
                    ? `${content.quantity} plate of ${content.name}`
                    : content.category === 'meat'
                    ? `${content.quantity} pieces of ${content.name}`
                    : `${content.quantity} pieces of ${content.name}`}
                </span>
              </div>
            ))}
          </div>
        ))}
            </Masonry>
            </ResponsiveMasonry>
      </Container>
    </div>
  );
};

export default Profile;
