import React, { useEffect, useReducer, useState } from 'react';
import Container from 'react-bootstrap/esm/Container';
import { Link, useLocation, useNavigate, useParams } from 'react-router-dom';
import Masonry, { ResponsiveMasonry } from 'react-responsive-masonry';
import './mybite.css';
import Navbar from '../navSection/Navbar';

import Footer from '../footerSection/Footer';
import SearchedItems from './SearchedItems';
import SearchBar from '../component/SearchBar';
import axios from 'axios';
import { useDispatch, useSelector } from 'react-redux';
import { fetchSuccess, searchedString } from '../redux/productsSearch';

const reducer = (state, action) => {
  switch (action.type) {
    case 'FETCH_STARTS':
      return { ...state, loading: true };
    case 'FETCH_SUCCESS':
      return {
        ...state,
        products: action.products,
        pages: action.pages,
        countProducts: action.countProducts,
        loading: false,
      };

    case 'FETCH_FAILURE':
      return { ...state, loading: false, error: action.payload };
    default:
      return;
  }
};
function SearchScreen() {
  const { search } = useLocation();
  const navigate = useNavigate();
  const sp = new URLSearchParams(search);
  const query = sp.get('query') || 'all';
  const price = sp.get('price') || 'all';
  const category = sp.get('category') || 'all';
  const page = sp.get('page') || 1;

  const { searchedProducts, searchedQuery } = useSelector(
    (state) => state.search
  );

  const [{ products, loading, error, countProducts, pages }, dispatch] =
    useReducer(reducer, {
      products: [],
      loading: false,
      error: null,
    });

  useEffect(
    () => {
    
      const handleSearch = async () => {
        try {
          const { data } = await axios.get(
            `http://localhost:5000/api/users?query=${query}&page=${page}&price=${price}&category=${category}`
          );
          console.log('this data', data);
          dispatch({"FETCH_SUCCESS": data})
        } catch (error) {
          console.log(error);
        }
      };
      handleSearch();
    },
    [query, category, price, page]
  );

  const filteredProducts = (filter) => {
   
    const filteredCategory = category.filter() || category;
    const filteredPrice = price.filter() || price;
    const filteredPage = page.filter() || page;
    const filteredQuery = query.filter() || query;

    return `/search?query=${filteredQuery}&page=${filteredPage}&price=${filteredPrice}&category=${filteredCategory}`;
  };

  const ctxdispatch = useDispatch();

 

  return (
    <div className="">
      <Navbar />
      <SearchBar />

      <div className="search-results">
        <p className="fw-bold">
          {searchedProducts?.length} kitchens selling{' '}
          <span className="border-bottom border-primary text-danger">
            {searchedQuery}
          </span>{' '}
          near you found
        </p>
      </div>
      <Container>
        <ResponsiveMasonry columnsCountBreakPoints={{ 350: 1, 750: 2, 900: 2 }}>
          <Masonry>
            {searchedProducts?.map((item, index) => (
              <Link
                to={`/kitchen/${item._id}`}
                className="text-decoration-none listings border-bottom  p-2"
                key={`${index}-${item._id}`}
              >
                {' '}
                <SearchedItems item={item} />
              </Link>
            ))}
          </Masonry>
        </ResponsiveMasonry>
      </Container>

      <div className="navigation-button">
        <button className="button1">see more</button>
      </div>
      <Footer />
    </div>
  );
}

export default SearchScreen; // Export the component
