import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import Cards from '../component/Cards';
import { Box, Skeleton } from '@mui/material';
import { useSelector } from 'react-redux';
import axios from 'axios';
import { api } from '../utils/apiConfig';

function Recommended(props) {
  const {product,   } = props;
  const [loading, setLoading]=useState(false)
  
  const { cartItems } = useSelector((state) => state.cart);
  const [availableProduct, setAvalaibleProduct] = useState([]);


  useEffect(() => {
    const relatedProducts = async () => {
      if (location.pathname !== product?.type || cartItems[0].name) {
        setLoading(true);
        const query =
          product?.type?.length > 2
            ? product?.type.slice(0, 4)
            : product?.type || cartItems?.map((item) => item.type.length > 2 ? item?.type.slice(0, 4)
            : item?.type);
        try {
          const { data } = await axios.post(
            `${api}/api/products/recommended/${product.userId ? product.userId : cartItems[0].userId}/${product._id || cartItems[0].userId}?query=${query}`
          );
          setAvalaibleProduct(data);
        setLoading(false)
        } catch (error) {
          setLoading(false)
          console.log(error);
        }
      }
    };
    relatedProducts();
  }, [cartItems, product, ]);
  return (
    <div>
          <div className=" my-3 ">
            {availableProduct && availableProduct.length > 0 && (
              <span className="font1bg fw-bold p-1 d-flex justify-content-center align-items-center w-100 mb-3">
                Recommended similar Items from this store
              </span>
            )}

            <div className="grid-container ">
              {(loading
                ? Array.from(new Array(5))
                : availableProduct && availableProduct
              )?.map((item, index) => (
                <Link
                  to={`/product/${item?._id}`}
                  key={`${index}`}
                  className="box text-decoration-none mb-2 grid-item"
                >
                  {' '}
                  {item ? (
                    <Cards loading={loading} item={item} />
                  ) : (
                    <div>
                      <Skeleton
                        variant="rectangular"
                        width={210}
                        height={118}
                      />
                      <Box sx={{ pt: 0.5 }}>
                        <Skeleton />
                        <Skeleton width="60%" />
                      </Box>
                    </div>
                  )}
                </Link>
              ))}
            </div>
          </div>
        </div>
  );
}

export default Recommended;
