import React, { useEffect, useState } from 'react';
import Button from 'react-bootstrap/Button';
import Navbar from '../navSection/Navbar';
import Form from 'react-bootstrap/Form';
import InputGroup from 'react-bootstrap/InputGroup';
import { Link, useNavigate } from 'react-router-dom';
import Footer from '../footerSection/Footer';
import axios from 'axios';

import { useDispatch, useSelector } from 'react-redux';
import {fetchSuccess } from '../redux/userSlice';
import Error from '../utils/Error';
import { toast } from 'react-toastify';

function SignIn() {
  const [name, setName] = useState('');
  const [password, setPassword] = useState('');
  const dispatch = useDispatch()
  const navigate = useNavigate()
  const { userInfo } = useSelector((state) => state.user);
  const [error, setError] = useState(null)


  const handleLogin =async (e) => {
    e.preventDefault();
    try {
      const { data } = await axios.post(`http://localhost:5000/api/users/signin`, {
        name: name,
        password: password,
      });
      dispatch(fetchSuccess(data))
   
    } catch (error) {
      toast.error('something went wrong, you must fill the form',{autoClose:true, theme: "colored", toastId:"unique"});
      setError(error.response.data.message)
      
      
    }
  };
 
  useEffect(()=>{
    if(userInfo?.user?.name){
      navigate('/')
    }
  })

  
  return (
    <div>
      <Navbar />

      <div
        className="d-flex justify-content-center flex-column align-items-center"
        style={{ width: '100%', height: '70vh' }}
      >
        <Button
          variant="danger"
          className="bg-danger opacity-75 p-1"
          style={{ borderRadius: '50%' }}
        >
          key
        </Button>
        <strong>Sign Up</strong>
        <Form onSubmit={handleLogin}>
          <InputGroup className="my-3">
            <InputGroup.Text>Name</InputGroup.Text>
            <Form.Control
              type="name"
              onChange={(e) => setName(e.target.value)}
            />
          </InputGroup>
          <InputGroup>
            <InputGroup.Text>Password</InputGroup.Text>
            <Form.Control
              type="password"
              onChange={(e) => setPassword(e.target.value)}
            />
          </InputGroup>
          <div className="d-grid my-3">
            <Button variant="success" className="bg-success" type="submit">
              Login
            </Button>
          </div>
        </Form>
        <div>
          <strong>
            {' '}
            New customer ? <Link to='/signup'> sign up here</Link>
          </strong>
        </div>
       {error !== null && <Error error={error}/>}
      </div>
      <Footer />
    </div>
  );
}

export default SignIn;
