import { createSlice } from '@reduxjs/toolkit';

export const productsSearch = createSlice({
  name: 'searchedProducts',

  initialState: {
    searchedProducts: [],
  },
  reducers: {
    fetchCart: (state, action) => {
      state.searchedProducts = action.payload;
    },

    addCart: (state, action) => {
      const newItem = action.payload;
      const existItem = state.cartItems.find((item) => item.id === newItem);

      const updatedItem = existItem
        ? state.cartItems.map((item) =>
            item._id === existItem._id
              ? { ...item, quantity: item.quantity + 1 }
              : item
          )
        : [...state.cartItens, { ...newItem, quantity: 1 }];
      return { ...state, cartItems: updatedItem };
    },
  },
});

export const { fetchCart } = productsSearch.actions;

export default productsSearch.reducer;
