import React, { useRef, useState, useEffect } from 'react';

const Tra = () => {
  const videoRef = useRef(null);
  const canvasRef = useRef(null);
  const [isTracking, setIsTracking] = useState(false);
  const [tracker, setTracker] = useState(null);
  const [boundingBox, setBoundingBox] = useState(null);

  useEffect(() => {
    const loadOpenCv = async () => {
      const script = document.createElement('script');
      script.src = 'https://docs.opencv.org/4.5.5/opencv.js';
      script.async = true;
      script.onload = onOpenCvReady;
      document.body.appendChild(script);
    };

    loadOpenCv();

    return () => {
      // Clean up OpenCV.js
      window.cv = null;
    };
  }, []);

  const onOpenCvReady = () => {
    // Initialize video
    const video = videoRef.current;
    navigator.mediaDevices.getUserMedia({ video: true })
      .then((stream) => {
        video.srcObject = stream;
      })
      .catch((error) => {
        console.error('Error accessing camera:', error);
      });

    // Initialize tracker
    const tracker = new window.cv.TrackerCSRT_create();
    setTracker(tracker);
  };

  const startTracking = () => {
    const video = videoRef.current;
    const canvas = canvasRef.current;
    const ctx = canvas.getContext('2d');
    
    // Start tracking
    setBoundingBox(window.cv.selectROI(video));

    setIsTracking(true);

    // Run object tracking loop
    const trackObject = () => {
      if (video.paused || video.ended) {
        return;
      }

      const frame = new window.cv.Mat(video.height, video.width, window.cv.CV_8UC4);
      const cap = new window.cv.VideoCapture(video);
      
      cap.read(frame);
      
      if (isTracking && boundingBox) {
        tracker.update(frame, boundingBox);
        const [x, y, w, h] = boundingBox;
        ctx.clearRect(0, 0, canvas.width, canvas.height);
        ctx.strokeStyle = '#00FF00';
        ctx.strokeRect(x, y, w, h);
      }

      requestAnimationFrame(trackObject);
    };

    trackObject();
  };

  const stopTracking = () => {
    setIsTracking(false);
  };

  return (
    <div>
      <video ref={videoRef} width="640" height="480" autoPlay></video>
      <canvas ref={canvasRef} width="640" height="480"></canvas>
      <button onClick={startTracking}>Start Tracking</button>
      <button onClick={stopTracking}>Stop Tracking</button>
    </div>
  );
};

export default Tra;
