import { createSlice } from '@reduxjs/toolkit';

export const productsSearch = createSlice({
  name: 'searchedProducts',

  initialState: {
    searchedProducts: [],
    searchedQuery: '',
  },
  reducers: {
    fetchCart: (state, action) => {
      const newItem = action.payload;
      
      const allCarts = state.searchedProducts?.map((item) => item._id === newItem._id);
      const quantity = allCarts ? allCarts.quantity +1 : 1
      if (!allCarts) {
        state.searchedProducts?.push(newItem);
      } else {
        state.allCarts = state.allCarts.quantity + 1;
      }
      return state.searchedProducts
    },

    searchedString: (state, action) => {
      state.searchedQuery = action.payload;
    },
  },
});

export const { fetchCart, searchedString } = productsSearch.actions;

export default productsSearch.reducer;
