import React, { useEffect } from 'react';

import NavSearch from '../navSection/NavSearch';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import { Link, useParams } from 'react-router-dom';
import axios from 'axios';

const product = {
  name: 'jolof rice',
  desc: 'nigeria jolof',
  price: 1200,
  _id: 'kzd',
  kitchenName: 'iyah-dolof kitchen',
  kitchenRating: 3.5,
  verified: true,
  physicalAddress: '30 udeji street lagos island',
  status: 'available',
  content: ['2 portions of rice', '1 chicken lap'],
  img: 'https://cdn.filestackcontent.com/5ngAPL80SuCvpsEb7S2f',
  imgs: [
    'https://eatwellabi.com/wp-content/uploads/2022/11/Jollof-rice-16.jpg',
    'https://massystoressvg.com/wp-content/uploads/2014/09/TRINI-STYLE-FRIED-CHICKEN-1024x767.jpg',
  ],
};

function Product() {
  const params = useParams();
  const { id } = params;

  useEffect(() => {
    const productHandler = async () => {
      try {
        const { data } = await axios.get();
      } catch (error) {
        console.log(error);
      }
    };
  }, []);
  return (
    <div className="" style={{ overflowX: 'hidden' }}>
      <NavSearch />
      <Row className="">
        <Col md={8} className="product  p-3">
          <Row>
            <Col md={10} className="rounded">
              <img
                src={product.img}
                alt=""
                className=""
                style={{ width: '100%' }}
              />
              <div className="d-flex my-2 gap-2 justify-content-center">
                {product.imgs.map((item, index) => (
                  <Link
                    key={index}
                    style={{ maxWidth: '50px', height: '50px' }}
                  >
                    {' '}
                    <img
                      src={item}
                      alt=""
                      style={{
                        maxWidth: '50px',
                        height: '50px',
                      }}
                      className="border rounded border-success"
                    />
                  </Link>
                ))}
              </div>
              <div className="bg-success d-flex flex-column  p-2 text-white rounded">
                <strong className="fs-5 fw-bold">
                  {' '}
                  Price: <span className='border-bottom border-grey'>N{product.price.toFixed(2)}</span>
                </strong>

                <strong
                  className="text-capitalize "
                  style={{ width: 'fit-content' }}
                >
                  Name: <span className='border-bottom border-grey'> {product.name}</span>
                </strong>

                <span>
                  <strong>Description: </strong>
                  {product.desc}
                </span>
                <div>
                  <strong>Content: </strong>
                  {product.content}
                </div>
              </div>
            </Col>
            <Col md={2}></Col>
          </Row>
        </Col>
        <Col md={4} className="">
          strterrefe
        </Col>
      </Row>
    </div>
  );
}

export default Product;
