import React, { useEffect, useState } from 'react';
import { Link, useLocation, useParams } from 'react-router-dom';
import KitchenBox from '../component/KitchenBox';
import './kitchen.css';
import Cards from '../component/Cards';
import Masonry, { ResponsiveMasonry } from 'react-responsive-masonry';
import Container from 'react-bootstrap/esm/Container';
import Navbar from '../navSection/Navbar';
import SearchBar from '../component/SearchBar';
import Col from 'react-bootstrap/Col';
import Row from 'react-bootstrap/Row';
import ListGroup from 'react-bootstrap/ListGroup';
import Form from 'react-bootstrap/Form';
import InputGroup from 'react-bootstrap/InputGroup';
import WidgetsIcon from '@mui/icons-material/Widgets';
import KeyboardArrowDownIcon from '@mui/icons-material/KeyboardArrowDown';
import axios from 'axios';



function Kitchen() {
  const [isSmallScreen, setIsSmallScreen] = useState(false);
  const location = useLocation();
  const params = useParams();
  const { id } = params;
  const [openSort, setOpenSort] = useState(false);
  const [kitchenData, setKitchenData] = useState([]);
  const [query, setQuery] = useState('');
  const [category, setCategory] = useState('');
  const [price, setPrice] = useState(0);
  const [page, setPage] = useState(1);
  

    const userKitchenProducts = async () => {
      try {
        const { data } = await axios.get(
          `http://localhost:5000/api/products/kitchen/${id}?price=${price}`
        );
        setKitchenData(data);
      } catch (error) {
        console.log(error);
      }
    };
  
    useEffect(() => {
      if (!price ) {
        setPage(1)
        userKitchenProducts();
      }
    }, [price,]); 
  
    const handleSearch = () => {
      if(query || price || category){
        navigate
      }
   
    };

  console.log('kitchenName:', kitchenData?.kitchenName);
  console.log('kitchen:', kitchenData?.products);

  useEffect(() => {
    const screenSieze = () => {
      setIsSmallScreen(window.innerWidth < 1200);
    };
    screenSieze();

    window.addEventListener('resize', screenSieze);

    return () => {
      window.removeEventListener('resize', screenSieze);
    };
  }, [openSort]);

  useEffect(() => {
    const body = document.body;
    setOpenSort((prevOpenSort) => {
      if (prevOpenSort) {
        body.classList.remove('no-scroll');
      } else {
        body.classList.add('no-scroll');
      }
      return !prevOpenSort;
    });

    return () => {
      body.classList.remove('no-scroll');
    };
  }, []);

  return (
    <div>
      <Navbar />
      <SearchBar setCategory={setCategory}  setQuery={setQuery} setPrice={setPrice} handleSearch={handleSearch} />

      <div className="my-2">
        <Container>
          <Row>
            <Col
              md={3}
              className={
                openSort && isSmallScreen
                  ? 'd-md-none openMenu'
                  : 'd-none d-md-flex'
              }
            >
              <div className="opensort-container">
                <ListGroup variant="flush">
                  {!openSort && (
                    <ListGroup.Item className="">
                      <div className="search-results1 p-3">
                        <div className="d-flex align-items-center gap-1 ">
                          <strong className="text-capitalize border-bottom text-secondary">
                            {kitchenData?.kitchenName}
                          </strong>
                          <img
                            src="https://cdn-icons-png.freepik.com/512/7641/7641727.png"
                            alt=""
                            style={{ width: '20px' }}
                          />
                        </div>
                        <span className="">
                          All products here belongs to{' '}
                          {kitchenData?.kitchenName} stores
                        </span>
                      </div>
                    </ListGroup.Item>
                  )}
                  <ListGroup.Item>
                    <strong>Price Range: </strong>
                    <Form.Range className="custom-range " />
                  </ListGroup.Item>
                  <ListGroup.Item>
                    <div>
                      <div className=" ">
                        <strong>Sort by:</strong>
                        <Form className="d-flex flex-wrap border custom-background">
                          <InputGroup
                            variant="light"
                            className=""
                            style={{ width: 'fit-content', border: 'none' }}
                          >
                            <InputGroup.Text className="d-flex   gap-1 border-0">
                              All <Form.Check name=''id='' type="checkbox" onChecked={(e)=>setQuery(e.target.Check)} />
                              
                            </InputGroup.Text>
                          </InputGroup>
                          <InputGroup style={{ width: 'fit-content' }}>
                            <InputGroup.Text className="border-0 d-flex gap-1">
                              SeaFoods <Form.Check />
                            </InputGroup.Text>
                          </InputGroup>
                          <InputGroup style={{ width: 'fit-content' }}>
                            <InputGroup.Text className="border-0 d-flex gap-1">
                              Desserts <Form.Check />
                            </InputGroup.Text>
                          </InputGroup>
                          <InputGroup style={{ width: 'fit-content' }}>
                            <InputGroup.Text className="border-0 d-flex gap-1">
                              Beverages <Form.Check />
                            </InputGroup.Text>
                          </InputGroup>
                          <InputGroup style={{ width: 'fit-content' }}>
                            <InputGroup.Text className="border-0 d-flex gap-1">
                              Salads <Form.Check />
                            </InputGroup.Text>
                          </InputGroup>
                          <InputGroup style={{ width: 'fit-content' }}>
                            <InputGroup.Text className="border-0 d-flex gap-1">
                              Pizza <Form.Check />
                            </InputGroup.Text>
                          </InputGroup>
                          <InputGroup style={{ width: 'fit-content' }}>
                            <InputGroup.Text className="border-0 d-flex gap-1">
                              Vegetarian Options <Form.Check />
                            </InputGroup.Text>
                          </InputGroup>

                          <InputGroup style={{ width: 'fit-content' }}>
                            <InputGroup.Text className="border-0 d-flex gap-1">
                              Sandwiches <Form.Check />
                            </InputGroup.Text>
                          </InputGroup>

                          <InputGroup style={{ width: 'fit-content' }}>
                            <InputGroup.Text className="border-0 d-flex gap-1">
                              Pasta <Form.Check />
                            </InputGroup.Text>
                          </InputGroup>
                          <InputGroup style={{ width: 'fit-content' }}>
                            <InputGroup.Text className="border-0 d-flex gap-1">
                              Burgers <Form.Check />
                            </InputGroup.Text>
                          </InputGroup>
                          <InputGroup style={{ width: 'fit-content' }}>
                            <InputGroup.Text className="border-0 d-flex gap-1">
                              Steaks <Form.Check />
                            </InputGroup.Text>
                          </InputGroup>
                        </Form>
                      </div>
                    </div>
                  </ListGroup.Item>
                </ListGroup>
              </div>
              {openSort && (
                <div
                  onClick={() => setOpenSort(false)}
                  className="closeOpenSort fs-2 text-danger"
                >
                  x
                </div>
              )}
            </Col>
            <Col
              md={9}
              className=""
              style={{
                borderLeft: !isSmallScreen ? '1px solid grey' : '',
                minHeight: '80vh',
              }}
            >
              <div className="search-results1 d-md-none p-3 mb-3">
                <div className="d-flex align-items-center gap-1 ">
                  <strong className="text-capitalize d-flex align-items-center gap-1 border-bottom text-secondary">
                    <span
                      onClick={() => setOpenSort(!openSort)}
                      style={{ width: 'fit-content' }}
                      className="d-flex bg-white border p-1 rounded align-items-center"
                    >
                      <WidgetsIcon />
                      <KeyboardArrowDownIcon className="" />
                    </span>{' '}
                    {kitchenData?.kitchenName}
                  </strong>
                  {kitchenData?.verified && (
                    <img
                      src="https://cdn-icons-png.freepik.com/512/7641/7641727.png"
                      alt=""
                      style={{ width: '20px' }}
                    />
                  )}
                </div>
                <span className="">
                  All products here belongs to {kitchenData?.kitchenName} stores
                </span>
              </div>
              <ResponsiveMasonry
                columnsCountBreakPoints={{ 300: 1, 350: 2, 750: 2, 1000: 3 }}
              >
                <Masonry gutter="10px">
                  {kitchenData?.products?.map((item) => (
                    <Link
                      style={{ maxHeight: '270px', minHeight: '270px' }}
                      className="text-decoration-none "
                      key={item._id}
                    >
                      <Cards item={item} />
                    </Link>
                  ))}
                </Masonry>
              </ResponsiveMasonry>
            </Col>
          </Row>
        </Container>
      </div>
    </div>
  );
}

export default Kitchen;
