import React, { useState } from 'react';
import Col from 'react-bootstrap/Col';
import Row from 'react-bootstrap/Row';
import ListGroup from 'react-bootstrap/ListGroup';
import { Link } from 'react-router-dom';
import CircleIcon from '@mui/icons-material/Circle';

import Button from 'react-bootstrap/Button';
import Rating from '../component/Rating';

import { useDispatch, useSelector } from 'react-redux';
import { addCart } from '../redux/cartSlice';
import CartError from '../utils/CartError';

function ProductDetails(props) {
  const { product } = props;
  const [currentImage, setCurrentImage] = useState(product?.imgs[0]);
  const [error, setError] = useState(null)

  const dispatch = useDispatch();
  const { cartItems } = useSelector((state) => state.cart);

  const addToCart = (cart) => {
    if (cartItems.length === 0) {
      const existItem = cartItems?.find((item) => item._id === product._id);
      const quantity = existItem ? existItem.quantity + 1 : 1;
      dispatch(addCart({ ...cart, quantity }));
    } else if (cartItems.length >= 1 && cart?.userId === cartItems[0]?.userId) {
      const existItem = cartItems?.find((item) => item._id === product._id);
      const quantity = existItem ? existItem.quantity + 1 : 1;
      dispatch(addCart({ ...cart, quantity }));
    } else {
      setError('you can only add items from same store ');
    }
  };

  return (
    <Row>
      <Col md={9} className="rounded">
        <div style={{ height: '60vh' }}>
          <img
            src={currentImage}
            alt=""
            className="rounded "
            style={{
              width: '100%',
              height: '100%',
              objectFit: 'contain',
            }}
          />
        </div>
        <div className="d-flex my-2 gap-2 justify-content-center">
          {product?.imgs?.map((item, index) => (
            <Link key={index} style={{ maxWidth: '50px', height: '50px' }}>
              {' '}
              <img
                src={item}
                alt=""
                style={{
                  maxWidth: '50px',
                  height: '50px',
                }}
                onClick={() => setCurrentImage(item)}
                className={
                  currentImage === item
                    ? 'border rounded border-success p-1'
                    : ''
                }
              />
            </Link>
          ))}
        </div>

        <div
          style={{ width: 'fit-content' }}
          className="bg-success d-flex flex-column my-3  p-2 text-white rounded text-capitalize bg-opacity-"
        >
          <strong className="fs-5 fw-bold">
            {' '}
            Price:{' '}
            <span className="border-bottom border-grey">
              N{product?.price.toFixed(2)}
            </span>
          </strong>

          <strong className="text-capitalize " style={{ width: 'fit-content' }}>
            Name:{' '}
            <span className="border-bottom border-grey"> {product?.name}</span>
          </strong>

          <span style={{ wordBreak: 'break-word' }}>
            <strong>Description: </strong>
            {product?.desc}
          </span>
          <div>
            <strong>Content: </strong>
            {product?.content}
          </div>
        </div>
      </Col>
      <Col md={3} className=" p-0">
        <ListGroup variant="flush">
          <ListGroup.Item className="d-flex align-items-center gap-1">
            <strong className="text-capitalize text-secondary">
              {product?.businessName}
            </strong>
            <img
              src="https://cdn-icons-png.freepik.com/512/7641/7641727.png"
              alt=""
              style={{ width: '20px' }}
            />
          </ListGroup.Item>
          <ListGroup.Item className="d-flex flex-column">
            <span>Physical Address: </span>
            {product?.physicalAddress}
          </ListGroup.Item>
          <ListGroup.Item className="d-flex flex-column">
            {' '}
            <span>
              Rating :{' '}
              {product?.businessRating > 0 ? product?.businessRating : 'none'}
            </span>
            <div className="d-flex align-items-center ">
              <Rating product={product} />
            </div>
          </ListGroup.Item>

          <ListGroup.Item>
            <span className="text-capitalize">
              {product?.status === 'available' ? (
                <span className="d-flex align-items-center gap-1">
                  online
                  <CircleIcon
                    className="text-success "
                    style={{ width: '13px', height: '13px' }}
                  />
                </span>
              ) : (
                'last seen:  5 minutes ago'
              )}
            </span>
            <div className="rounded  d-flex flex-column">
              <span className="mb-1 ">
                {' '}
                {product?.verified
                  ? 'this user is verified'
                  : 'this user is not verified'}
              </span>
              <Button
                onClick={() => addToCart(product)}
                className="fw-bold"
                variant="success"
              >
                Add To Tray
              </Button>
            </div>
          </ListGroup.Item>
        </ListGroup>
      </Col>
      {error &&
      <div style={{position:"absolute", left:0, top:0, width:"100vw", backgroundColor:"white", height}}>
        <CartError error={error}/>
        </div>}
    </Row>
  );
}

export default ProductDetails;
