import React, { useEffect, useState } from 'react';
import Navbar from '../navSection/Navbar';
import Map from '../component/Map';
import HouseOutlinedIcon from '@mui/icons-material/HouseOutlined';
import Cards from '../component/Cards';
import { Link } from 'react-router-dom';
import './homePage.css';
import HomeFeatures from './HomeFeatures';
import Footer from '../footerSection/Footer';
import axios from 'axios';
import UseEffectData from '../fetch/UseEffectData';

const data = [
  {
    name: 'jolof rice',
    desc: 'nigeria jolof',
    price: 1200,
    _id: 'kzd',
    kitchenName: 'iyah-dolof kitchen',
    kitchenRating: 3.5,
    verified: true,
    physicalAddress: '30 udeji street lagos island',
    status: 'available',
    content: ['2 portions of rice', '1 chicken lap'],
    imgs: ['/images/okroandfufu.jpeg'],
  },
  {
    name: 'amala and ewedu',
    desc: 'hot delicious amala and ewedu',
    content: [
      '1 chicken lap',
      'a bow of soup',
      '2 wraps of amala',
      '4 pieces of beef',
    ],
    price: 700,
    _id: 'kzd28',
    kitchenName: 'iyah-dolof kitchen',
    kitchenRating: 1.5,
    verified: true,
    physicalAddress: '30 udeji street lagos island',
    status: 'available',
    imgs: ['/images/ghanajolof.jpg'],
  },
];

const carouselData = [
  {
    kitchenImg:
      'https://www.plotprojects.com/wp-content/uploads/2023/05/rsz_best_delivery_management_software.jpg',
    kitchenName: 'delux kitchen',
    deliveryRate: 30,
    rating: 3.5,
    verified: true,
    km: 10,
  },
  {
    kitchenImg:
      'https://img.freepik.com/premium-vector/online-parcel-delivery-service-concept-mobile-app-concept-hand-holding-phone-with-parcel-delivery-application-display-courier-carries-cart-with-boxes-orders-truckflat-illustration_608021-195.jpg?w=360',
    kitchenName: 'chef bobby',
    deliveryRate: 45,
    rating: 4.5,
    verified: true,
    km: 47,
  },
];

function HomePage() {
  const page = 1;
  const data =UseEffectData(`http://localhost:5000/api/products?${page}`)
  const carouselData =UseEffectData(`http://localhost:5000/api/users?${page}`)
  return (
    <div>
      <Navbar />
      <Map />
      <div className="my-5 d-flex justify-content-center">
        {' '}
        <h3 className="border-bottom border-secondary">
          We diliver to your door-step
          <HouseOutlinedIcon className="fs-1 text-success" />
        </h3>
      </div>
      <div className="d-flex gap-3 justify-content-center align-items-center my-5 ">
        {data.map((item) => (
          <Link
            to={`/product/${item._id}`}
            key={item._id}
            className="box text-decoration-none "
            style={{height:"150px", border:"1px solid red"}}
          >
            <Cards item={item} />
          </Link>
        ))}
      </div>
      <div className="d-flex justify-content-center text-secondary border-top border-grey py-3">
        <div className="d-flex my-5 flex-column">
          <Link className=" text-decoration-none fs-5  fw-bold text-secondary text-capitalize border-bottom border-danger">
            See all available kitchens
          </Link>
          <span className="">195 stores are currently online</span>
        </div>
      </div>
      <div
        
        className="rowParent2   border-bottom border-secondary"
      >
        <HomeFeatures carouselData={carouselData} />
      </div>
      <div className=" p-2">
        <Footer />
      </div>
    </div>
  );
}

export default HomePage;
