import React from 'react';
import { useLocation } from 'react-router-dom';

function Cards({ item }) {
  const location = useLocation();
  return (
    <div
      className=""
      style={{ minHeight: '100%', maxHeight: '300px', border:"5px solid green" }}
    >
      <div className="">
        <div className="cardBox">
          {' '}
          <img
            src={item.imgs[0]}
            alt=""
            style={{
              width: '100%',
              height: location.pathname === '/kitchen' ? '180px' : '180px',
              transition: 'ease-in-out',
            }}
            className=""
          />
        </div>
        <div style={{ height: '' }} className="">
          <div className="d-flex align-items-center flex-column justify-content-center">
            <span className="fw-bold text-capitalize text-secondary">
              {item.name.length > 15
                ? `${item.name.slice(0, 15)}...`
                : item.name}
            </span>
            <span className="text-secondary">
              {item.desc.length > 15
                ? `${item.desc.slice(0, 15)}...`
                : item.desc}
            </span>
            <strong className=" text-success" >
              N{item.price.toFixed(2)}
            </strong>
          </div>
        </div>
      </div>
    </div>
  );
}

export default Cards;
