import React, { useEffect, useState } from 'react';
import Map from '../component/Map';
import Navbar from '../navSection/Navbar';
import Footer from '../footerSection/Footer';
import SearchedItems from '../dalle/SearchedItems';
import { Link, useLocation } from 'react-router-dom';
import Masonry, { ResponsiveMasonry } from 'react-responsive-masonry';
import Container from 'react-bootstrap/esm/Container';
import axios from 'axios';

const kitchens = [
  {
    name: 'Road Masters Riders',
    timeOpen: 'Open from Mon - Sun 6:00am - 10:00pm',
    km: 17,
    status: 'Available',
    img: '/images/rider1.jpg',
    deliveryRate: 30,
  },
  {
    name: 'Free & Fair Riders',
    timeOpen: 'Open from Mon - Sun 6:00am - 11:00pm',
    deliveryRate: 90,
    km: 105,
    status: 'Not available',
    img: '/images/rider2.jpg',
  },
  {
    name: 'Ultimate Riders',
    timeOpen: 'Open from Mon - Sun 11:00am - 12:00pm',
    km: 5,
    status: ' available',
    img: '/images/rider3.jpeg',
    deliveryRate: 90,
  },
];

function RidersPage() {
  const {search} = useLocation()
  const sp = new URLSearchParams(search)
  const query = sp.get("query")|| ''
  const page = sp.get("page")|| 1
  const [riders, setRiders] =useState([])

  useEffect(()=>{
    const handleSearch = async () => {
      try {
        const { data } = await axios.get(
          `http://localhost:5000/api/users/riders?query=${query}&page=${page}`
        );
        console.log('ride', data);
        setRiders(data);
      } catch (error) {
        console.log(error)
      }
    }
    handleSearch()
  },[query,page])
  return (
    <div>
      <Navbar />
      <Map />
      <div className="search-results">
        <p className="fw-bold fs-5">({kitchens?.length}) Riders found near you </p>
      </div>
      <Container>
        <ResponsiveMasonry columnsCountBreakPoints={{ 350: 1, 750: 2, 900: 2 }}>
          <Masonry>
            {kitchens?.map((item, index) => (
              <Link
                className="text-decoration-none listings border-bottom  p-2"
                to=""
                key={index}
              >
                {' '}
                <SearchedItems item={item} />
              </Link>
            ))}
          </Masonry>
        </ResponsiveMasonry>
      </Container>

      <div className="navigation-button">
        <button className="button1">see more</button>
      </div>
      <Footer />
    </div>
  );
}

export default RidersPage;
