import React from 'react';
import { Link, useLocation } from 'react-router-dom';
import KitchenBox from '../component/KitchenBox';
import './kitchen.css';
import Cards from '../component/Cards';
import Masonry, { ResponsiveMasonry } from 'react-responsive-masonry';
import Container from 'react-bootstrap/esm/Container';
import Navbar from '../navSection/Navbar';
import SearchBar from '../component/SearchBar';
import Col from 'react-bootstrap/Col';
import Row from 'react-bootstrap/Row';

const carouselData = [
  {
    imgs: [
      'https://www.plotprojects.com/wp-content/uploads/2023/05/rsz_best_delivery_management_software.jpg',
    ],
    name: 'jollof rice',
    price: 4550,
    desc: 'The best nigeria jollof rice',
  },
  {
    imgs: [
      'https://img.freepik.com/premium-vector/online-parcel-delivery-service-concept-mobile-app-concept-hand-holding-phone-with-parcel-delivery-application-display-courier-carries-cart-with-boxes-orders-truckflat-illustration_608021-195.jpg?w=360',
    ],
    name: 'okro and panded yam',
    price: 7530,
    desc: 'okro and pando combo',
  },
];

function Kitchen() {
  const location = useLocation();
  return (
    <div>
      <Navbar/>
      <SearchBar/>
     
      <div className='my-5'>
        <Container>
          <Row>
            <Col>
            <div className=''>
            <ListGroup variant="flush">
          <ListGroup.Item className="d-flex align-items-center gap-1">
            <strong className="text-capitalize text-secondary">
              {product.kitchenName}
            </strong>
            <img
              src="https://cdn-icons-png.freepik.com/512/7641/7641727.png"
              alt=""
              style={{ width: '20px' }}
            />
          </ListGroup.Item>
          </ListGroup>
            </div>
            </Col>
            <Col></Col>
          </Row>
          <ResponsiveMasonry
            columnsCountBreakPoints={{ 350: 2, 750: 4, 900: 5 }}
          >
            <Masonry gutter='10px'>
              {carouselData.map((item) => (
                <Link  style={{maxHeight:"270px", minHeight:"270px"}} className='text-decoration-none ' key={item._id}>
                  <Cards item={item} />
                </Link>
              ))}
            </Masonry>
          </ResponsiveMasonry>
        </Container>
      </div>
    </div>
  );
}

export default Kitchen;
