import React, { useEffect, useState } from 'react';
import Navbar from '../navSection/Navbar';
import Map from '../component/Map';
import HouseOutlinedIcon from '@mui/icons-material/HouseOutlined';
import Cards from '../component/Cards';
import { Link, useNavigate } from 'react-router-dom';
import './homePage.css';
import HomeFeatures from './HomeFeatures';
import Footer from '../footerSection/Footer';

import axios from 'axios';
import { useSelector } from 'react-redux';

function HomePage(props) {
  const [data, setData] = useState([]);
  const [carouselData, setCarouselData] = useState([]);
  const randomNum = Math.floor(Math.random() * 500);
  const page = 'all';
  const { cartItems } = useSelector((state) => state.cart);
  const userId = cartItems.length > 0 ? cartItems[0].userId : '';
  const navigate = useNavigate()
  const [currentSlide, setCurrentSlide] = useState(0);


  useEffect(() => {
    const fetchData = async () => {
      try {
        const productResponse = await axios.get(
          `http://localhost:5000/api/products?query=${userId}`
        );

        setData(productResponse.data);
      } catch (error) {
        console.log('Error fetching data:', error);
      }
    };

    fetchData();
  }, [userId]);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const userResponse = await axios.get(
          `http://localhost:5000/api/users?query=${page}`
        );

        setCarouselData(userResponse.data);
      } catch (error) {
        console.log('Error fetching data:', error);
      }
    };

    fetchData();
  }, []);
  const prevSlide = () => {
    setCurrentSlide(currentSlide === 0 ? 2 : (prev) => prev - 1);
  };
  const nextSlide = () => {
    setCurrentSlide(currentSlide === 2 ? 0 : (prev) => prev + 1);
  };
  return (
    <div>
      <Navbar />
      <Map />
      <div className="my-5 d-flex justify-content-center">
        {' '}
        <h3 className="border-bottom border-secondary">
          We diliver to your door-step
          <HouseOutlinedIcon className="fs-1 text-success" />
        </h3>
      </div>
      {cartItems?.length > 0 &&<span className="font1bg fw-bold p-1 d-flex justify-content-center align-items-center">Recommended Items from the store in your cart</span>}
      <div
        className="d-flex gap-3 justify-content-cente align-items-center my-5 "
        style={{ overflowX: 'hidden' }}
      >
        {data.map((item, index) => (
          <Link
            to={`/product/${item._id}`}
            key={`${item._id}-${index}`}
            className="box text-decoration-none "
          
            style={{ height: '270px', minWidth: '200px',  transform: `translateX(-${currentSlide * 200})` }}
          >
            <Cards item={item} />
          </Link>
        ))}
      </div>
      <div  className="d-flex justify-content-center text-secondary border-top border-grey py-3">
        <div className="d-flex my-5 flex-column">
          <Link to='/search' className=" text-decoration-none fs-5  fw-bold text-secondary text-capitalize border-bottom border-danger">
            See all available kitchens
          </Link>
          <span className="">
            {carouselData.length < 50
              ? carouselData.length + randomNum
              : carouselData}{' '}
            stores are currently online
          </span>
        </div>
      </div>
      <div className="rowParent2   border-bottom border-secondary">
        <HomeFeatures carouselData={carouselData} />
      </div>
      <div className=" p-2">
        <Footer />
      </div>
    </div>
  );
}

export default HomePage;
