import React from 'react';
import Card from 'react-bootstrap/Card';

function Cards({ item }) {
  return (
    <div
      className="border border-success"
      style={{ minHeight: '100%', maxHeight: '100%' }}
    >
      <div style={{ minHeight: '100%', maxHeight: '100%' }} className="">
        <div className=' border border-danger' style={{ minHeight: '600%', height: '60%', width: '100%' }}>
          {' '}
          <img
            src={item.imgs[0]}
            alt=""
            
            className=""
          />
        </div>
        <div style={{ height: '' }} className="">
          <div className="d-flex align-items-center flex-column justify-content-center">
            <span className="fw-bold text-capitalize text-secondary">
              {item.name.length > 15
                ? `${item.name.slice(0, 15)}...`
                : item.name}
            </span>
            <span className="text-secondary">
              {item.desc.length > 15
                ? `${item.desc.slice(0, 15)}...`
                : item.desc}
            </span>
            <strong className=" text-success" class>
              N{item.price.toFixed(2)}
            </strong>
          </div>
        </div>
      </div>
    </div>
  );
}

export default Cards;
