import React, { useEffect } from 'react';
import Navbar from '../navSection/Navbar';
import Map from '../component/Map';
import HouseOutlinedIcon from '@mui/icons-material/HouseOutlined';
import Cards from '../component/Cards';
import { Link } from 'react-router-dom';
import './homePage.css';
import HomeFeatures from './HomeFeatures';
import Footer from '../footerSection/Footer';

import UseEffectData from '../fetch/UseEffectData';



function HomePage() {
  const [productData, setProductData] = useState([]);
  const [userData, setUserData] = useState([]);
  const page = 1;

  useEffect(() => {
    const fetchData = async () => {
      try {
        const productResponse = await axios.get(`http://localhost:5000/api/products?page=${page}`);
        const userResponse = await axios.get(`http://localhost:5000/api/users?page=${page}`);
        setProductData(productResponse.data);
        setUserData(userResponse.data);
      } catch (error) {
        console.log('Error fetching data:', error);
      }
    };

    fetchData();
  }, [page]);

 
  const data = UseEffectData(`http://localhost:5000/api/products?page=${page}`);
  const carouselData = UseEffectData(`http://localhost:5000/api/users?page=${page}`);

  console.log("data products", data)
  return (
    <div>
      <Navbar />
      <Map />
      <div className="my-5 d-flex justify-content-center">
        {' '}
        <h3 className="border-bottom border-secondary">
          We diliver to your door-step
          <HouseOutlinedIcon className="fs-1 text-success" />
        </h3>
      </div>
      <div className="d-flex gap-3 justify-content-center align-items-center my-5 " style={{overflowX:"scroll"}}>
        {data.map((item, index) => (
          <Link
            to={`/product/${item._id}`}
            key={`${item._id}-${index}`}
            className="box text-decoration-none "
            style={{height:"270px",  minWidth:"200px"}}
          >
            <Cards item={item} />
          </Link>
        ))}
      </div>
      <div className="d-flex justify-content-center text-secondary border-top border-grey py-3">
        <div className="d-flex my-5 flex-column">
          <Link className=" text-decoration-none fs-5  fw-bold text-secondary text-capitalize border-bottom border-danger">
            See all available kitchens
          </Link>
          <span className="">195 stores are currently online</span>
        </div>
      </div>
      <div
        
        className="rowParent2   border-bottom border-secondary"
      >
        <HomeFeatures carouselData={carouselData} />
      </div>
      <div className=" p-2">
        <Footer />
      </div>
    </div>
  );
}

export default HomePage;
