import React, { useEffect, useState } from 'react';
import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import Button from 'react-bootstrap/Button';
import Badge from 'react-bootstrap/Badge';
import Navbar from '../navSection/Navbar';
import AccountBalanceIcon from '@mui/icons-material/AccountBalance';
import SearchBar from '../component/SearchBar';
import Masonry, { ResponsiveMasonry } from 'react-responsive-masonry';
import './profile.css';
import Footer from '../footerSection/Footer';
import axios from 'axios';
import { Link, useNavigate } from 'react-router-dom';
import { useDispatch, useSelector } from 'react-redux';
import NavSearch from '../navSection/NavSearch';
import WalletIcon from '@mui/icons-material/Wallet';
import MoneyOffIcon from '@mui/icons-material/MoneyOff';
import AddCardIcon from '@mui/icons-material/AddCard';
import AttachMoneyIcon from '@mui/icons-material/AttachMoney';
import { fetchSuccess } from '../redux/userSlice';
const Profile = () => {
  const [data, setData] = useState([]);
  const [accountCreate, setAccountCreate] = useState({});
  const { userInfo } = useSelector((state) => state.user);
  const navigate = useNavigate();
  const [openWallet, setOpenWallet] = useState(false);
  const [open, setOpen] = useState(false);
  const [arl, Setarl] = useState(false);
  const dispatch = useDispatch();
  const [balance, setBalance] = useState(null)

  useEffect(() => {
    const handleOrder = async () => {
      try {
        const { data } = await axios.get(
          `http://localhost:5000/api/orders/allorders/${userInfo?.user?._id}`
        );

        setData(data);
      } catch (error) {
        console.log(error);
      }
    };
    handleOrder();
  }, [userInfo?.user?._id]);

  useEffect(() => {
    if (!userInfo.user?._id) {
      navigate(`/signin`);
    }
  });

  useEffect(( )=>{
     try {
      const {data} = axios.get(`http://localhost:5000/users/id/${userInfo.user?._id}`);
      setBalance()
     } catch (error) {
      
     }
  })

  const handleWalletDetails = async () => {
    try {
      const { data } = await axios.get(
        `http://localhost:5000/api/users/account/find/${userInfo?.user?._id}`
      );
      Setarl(data);
      console.log(data);
      setOpenWallet(true);
    } catch (error) {
      console.error(error);
    }
  };
  const handleCreateWallet = async () => {
    try {
      const { data } = await axios.post(
        `http://localhost:5000/api/payment/create/${userInfo?.user?._id}`
      );

      Setarl(data);
      console.log(data);
      setOpenWallet(true);

      setOpen(false);
      dispatch(
        fetchSuccess({ ...userInfo, user: { ...userInfo.user, wallet: true } })
      );
    } catch (error) {
      console.error(error);
    }
  };
  
  return (
    <div>
      <NavSearch />
      <Container className="">
        <Row className="balance-inf my-2">
          <Col className=" p-3 border ">
            <h3>
              Hello{' '}
              {userInfo?.user?.name ? userInfo?.user?.name.split(' ')[0] : ''} !
            </h3>

            <Row className="d-flex  justify-content-between flex-wrap gap-3">
              {' '}
              <Col md={5} className="d-flex flex-column ">
                {' '}
                {!open && (
                  <div className="d-flex flex-column mb-2">
                    {' '}
                    Available balance:
                    <strong> N{userInfo?.user?.balance?.toFixed(2)}</strong>
                  </div>
                )}
                {userInfo?.user?.wallet !== false ? (
                  <div className="d-flex align-items-center gap-3">
                    {' '}
                    <Button
                      variant="success"
                      className="fw-bold d-none d-md-flex align-items-center"
                      onClick={handleWalletDetails}
                    >
                      <AttachMoneyIcon />
                      Fund wallet
                    </Button>
                    <Button
                      variant="success"
                      className="fw-bold d-md-none d-flex align-items-center"
                      onClick={handleWalletDetails}
                    >
                      <AttachMoneyIcon />
                      Deposit
                    </Button>
                    <Button
                      variant="white"
                      className="fw-bold border text-success border-success d-flex align-items-center"
                    >
                      <AccountBalanceIcon className="text-dark" />
                      CashOut
                    </Button>
                  </div>
                ) : (
                  <div>
                    {' '}
                    {!open && (
                      <Button
                        variant="success"
                        className="fw-bold d-flex gap-1 align-items-center"
                        onClick={() => setOpen(true)}
                      >
                        <AddCardIcon />
                        Create wallet
                      </Button>
                    )}
                    {open && (
                      <div
                        className="d-flex align-items-center justify-content-center flex-column gap-3 border border-success rounded p-1"
                        style={{ backgroundColor: 'lightgrey' }}
                      >
                        <Link className="fw-bold fs-5">
                          Create a new wallet!
                        </Link>
                        <strong style={{ fontSize: '12px' }}>
                          This is your personal wallet, which you can use to
                          make purchases on our platform. By clicking 'Okay',
                          you agree to our terms and conditions.
                        </strong>
                        <div className="d-flex gap-2 align-items-center">
                          <Button
                            variant="white"
                            className="border-danger"
                            onClick={() => setOpen(false)}
                          >
                            Cancel
                          </Button>
                          <Button
                            onClick={handleCreateWallet}
                            variant="danger"
                            className="bg-danger"
                          >
                            OK
                          </Button>
                        </div>
                      </div>
                    )}
                  </div>
                )}
              </Col>
              <Col
                md={5}
                className="text-end  d-flex flex-column text-secondary"
              >
                {!openWallet && (
                  <div className="d-none d-md-block">
                    <div>
                      <Button variant="link mb-2 text-success">History</Button>
                    </div>
                    <Button
                      onClick={() => {
                        handleWalletDetails();
                        setOpenWallet(true);
                      }}
                      variant="success"
                      className="fw-bold"
                      style={{ width: 'fit-content' }}
                    >
                      Wallet details
                    </Button>
                  </div>
                )}

                {openWallet && (
                  <>
                    {arl.accountNumber && (
                      <div
                        className="d-flex acct text-start flex-column text-secondary"
                        style={{ fontSize: '14px' }}
                      >
                        <strong
                          variant=" text-success"
                          className="text-capitalize"
                        >
                          Name: {userInfo.user.name}
                        </strong>
                        <strong variant=" text-success">
                          Bank: {arl.bank}
                        </strong>
                        <strong variant=" text-success">
                          Account Number: {arl.accountNumber}
                        </strong>
                        <span
                          className="p-2 border rounded"
                          style={{
                            fontSize: '11px',
                            backgroundColor: 'lightgrey',
                          }}
                        >
                          this account is generated for you to fund your wallet.{' '}
                          <br />
                          transaction made to this account will show up in your
                          wallet
                          <div className="text-end">
                            {' '}
                            <Button
                              onClick={() => setOpenWallet(false)}
                              variant="white"
                              className="border-success rounded"
                              style={{ width: 'fit-content' }}
                            >
                              close
                            </Button>
                          </div>
                        </span>
                      </div>
                    )}
                    {!arl?.accountNumber && (
                      <div>
                        you do not have wallet to show. <Link>Create here</Link>
                      </div>
                    )}
                  </>
                )}
              </Col>
            </Row>

            <div></div>
          </Col>
        </Row>
        <SearchBar />
        <div className="my-3 ">
          <div className="text-center mb-3 border-bottom border-grey">
            {' '}
            <strong className="text-success fw-bold ">
              New Orders
              <Badge pill bg="danger">
                {data.length}
              </Badge>
            </strong>
          </div>
          <ResponsiveMasonry
            columnsCountBreakPoints={{ 350: 1, 750: 2, 900: 2 }}
          >
            <Masonry gutter="20px">
              {data?.map((item, index) => (
                <Link
                  to={`/order/${item?._id}`}
                  key={`${index}-${item?.productId}`}
                  className="d-flex align-items-center imageCardParent   gap-2 "
                  style={{ position: 'relative' }}
                >
                  <div className="cardImage">
                    <img
                      src={item.imgs}
                      alt=""
                      style={{
                        objectFit: 'cover',
                        borderRadius: '10px 0px 0px 10px',
                      }}
                    />
                  </div>
                  <div className="fw-bold d-flex gap-1 flex-wrap">
                    {item?.content?.slice(0, 3).map((content, index) => (
                      <div className="text-dark" key={index}>
                        {
                          <span>
                            {content.category === 'food'
                              ? `${content.quantity} plate of ${content.name}, `
                              : content.category === 'meat'
                              ? ` ${content.quantity} pieces of ${content.name}, `
                              : `${content.quantity} pieces of ${content.name}${
                                  item.content?.length > 3 ? '...' : ''
                                } `}
                          </span>
                        }
                      </div>
                    ))}
                  </div>
                  <div className="opacity-40">
                    <img
                      src="https://wonderingfair.files.wordpress.com/2014/12/new-logo.gif"
                      alt=""
                      style={{
                        width: '40px',
                        height: '40px',
                        position: 'absolute',
                        top: 0,
                        right: 6,
                      }}
                    />
                  </div>
                </Link>
              ))}
            </Masonry>
          </ResponsiveMasonry>
        </div>
      </Container>
      <Footer />
    </div>
  );
};

export default Profile;
