import React from 'react';
import HighlightOffOutlinedIcon from '@mui/icons-material/HighlightOffOutlined';
import ListGroup from 'react-bootstrap/ListGroup';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import Card from 'react-bootstrap/Card';
import { useLocation } from 'react-router-dom';

const cartItems = [
  {
    name: 'jolof rice',
    desc: 'nigeria jolof',
    price: 1200,
    _id: 'kzd',
    kitchenName: 'iyah-dolof kitchen',
    kitchenRating: 3.5,
    verified: false,
    physicalAddress: '30 udeji street lagos island',
    status: 'available',
    content: ['2 portions of rice', '1 chicken lap'],
    imgs: [
      '/images/ghanajolof.jpg',
      '/images/giudau.webp',
      '/images/fdjfkgfgfg.jpg',
    ],
  },
  {
    name: 'jolof rice',
    desc: 'nigeria jolof',
    price: 1200,
    _id: 'kzd',
    kitchenName: 'iyah-dolof kitchen',
    kitchenRating: 3.5,
    verified: false,
    physicalAddress: '30 udeji street lagos island',
    status: 'available',
    content: ['2 portions of rice', '1 chicken lap'],
    imgs: [
      '/images/ghanajolof.jpg',
      '/images/giudau.webp',
      '/images/fdjfkgfgfg.jpg',
    ],
  },
  {
    name: 'jolof rice',
    desc: 'nigeria jolof',
    price: 1200,
    _id: 'kzd',
    kitchenName: 'iyah-dolof kitchen',
    kitchenRating: 3.5,
    verified: false,
    physicalAddress: '30 udeji street lagos island',
    status: 'available',
    content: ['2 portions of rice', '1 chicken lap'],
    imgs: [
      '/images/ghanajolof.jpg',
      '/images/giudau.webp',
      '/images/fdjfkgfgfg.jpg',
    ],
  },
];

function CartCard() {
  const location = useLocation();
  return (
    <div>
      {location.pathname !== '/cart' && (
        <Card>
          <Card.Body>
            <strong>Total(3 Items): N157000.00</strong>
          </Card.Body>
        </Card>
      )}

      <ListGroup className="mt-2 border">
        {location.pathname !== '/cart' && (
          <span className="text-center fw-bold border-bottom border-dark fs-5">
            Your Cart Items
          </span>
        )}

        {cartItems.slice(0, 3).map((item) => (
          <ListGroup.Item key={item._id} style={{ position: 'relative', maxHeight:"200px" }}>
            <Row>
              <Col md={7} style={{height:"100"}}>
                {' '}
                <img src={item.imgs[0]} alt="" style={{ width: '100%', height:"100%"  }} />
              </Col>
              <Col
                md={5}
                className="d-flex justify-content-center align-items-center flex-column "
              >
                <span className="fw-bold text-capitalize text-secondary">
                  {item.name}
                </span>

                <strong className=" text-success" class>
                  N{item.price.toFixed(2)}
                </strong>
                <div className="d-flex align-items-center gap-1">
                  {' '}
                  <span
                    style={{
                      width: '30px',
                      height: '30px',
                      borderRadius: '50%',
                    }}
                    className="fw-bold bg-success d-flex justify-content-center text-white"
                  >
                    -
                  </span>{' '}
                  6{' '}
                  <span
                    style={{
                      width: '30px',
                      height: '30px',
                      borderRadius: '50%',
                    }}
                    className="fw-bold bg-success d-flex justify-content-center text-white"
                  >
                    +
                  </span>{' '}
                </div>
              </Col>
            </Row>
            <span
              style={{
                position: 'absolute',
                top: 1,
                right: 2,
                cursor: 'pointer',
              }}
              className="text-danger opacity-25 "
            >
              <HighlightOffOutlinedIcon />
            </span>
          </ListGroup.Item>
        ))}
        {/* <ListGroup.Item>
                  <span>Cart Items</span>
                </ListGroup.Item> */}
      </ListGroup>
    </div>
  );
}

export default CartCard;
