import React from 'react';
import ListGroup from 'react-bootstrap/ListGroup';

function AcctHistory(props) {
  const { history, userInfo } = props;
  return (
    <ListGroup variant='flush' className='bg-white px-3 border border-grey rounded'>
     
      {history &&
        history.length > 0 &&
        history.map((item, index) => (
          <ListGroup.Item className='mb-2' >
            <span
              key={index}
              className={
                item.buyerId === userInfo?.user._id
                  ? `text-success fw-bold`
                  : `fw-bold text-success`
              }
            >
              {item.buyerId === userInfo?.user._id
                ? `-N${item?.amount.toFixed(2)}`
                : `+N${item?.amount.toFixed(2)}`}{' '}
              {item.buyerId === userInfo.user?._id
                ? `deposited and sent to ${item.businessName}`
                : `Order from ${item.buyerName}`}
            </span>
          </ListGroup.Item>
        ))}
    </ListGroup>
  );
}

export default AcctHistory;
