import React from 'react';
import Container from 'react-bootstrap/Container';
import MenuIcon from '@mui/icons-material/Menu';
import CircleNotificationsIcon from '@mui/icons-material/CircleNotifications';
import AccountCircleIcon from '@mui/icons-material/AccountCircle';
import { Link, useLocation, useNavigate } from 'react-router-dom';
import { useSelector } from 'react-redux';
import Badge  from 'react-bootstrap/Badge';

function Navbar() {
  const navigate = useNavigate();
  const location = useLocation();
  const { userInfo } = useSelector((state) => state.user);
  const { cartItems } = useSelector((state) => state.cart);

  return (
    <div className="bg-success ">
      <Container className="text-white py-1 d-flex justify-content-between align-items-center">
        <div className="d-flex gap-3 align-items-center navbarIcon px-2">
          <MenuIcon className="fs-2 fw-bold" />
          <Link to="/" className="text-decoration-none text-dark">
            <strong className="fw-bold fs-5">M-bite</strong>
          </Link>
        </div>
        <div className="d-flex align-items-center gap-3">
          <div style={{ position: 'relative' }}>
            <CircleNotificationsIcon
              onClick={() => navigate('/cart')}
              className={
                location.pathname === '/cart'
                  ? 'fs-2 fw-bold border border-white text-white color-white border-rounded'
                  : 'fs-2 fw-bold '
              }
            />
           
            <Badge
              pill
              bg="danger"
              className=""
              style={{ position: 'absolute', right: -5, top:-2 }}
            >
              {cartItems?.length}
            </Badge>
          </div>

          <div className="d-flex gap-1 align-items-center">
            <AccountCircleIcon className="fs-2 fw-bold" />
            {userInfo?.user?.name ? (
              <span
                onClick={() => navigate('/profile')}
                className="d-none d-md-flex fw-bold text-capitalize"
              >
                {userInfo?.user?.name}
              </span>
            ) : (
              <Link className="text-white" to="/signin">
                Log-in
              </Link>
            )}
          </div>
        </div>
      </Container>
    </div>
  );
}

export default Navbar;
