import React, { useEffect, useState } from 'react';

import NavSearch from '../navSection/NavSearch';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import { Link, useParams } from 'react-router-dom';
import axios from 'axios';
import ListGroup from 'react-bootstrap/ListGroup';
import StarOutlineIcon from '@mui/icons-material/StarOutline';

import './product.css';
import Button from 'react-bootstrap/Button';
import Container from 'react-bootstrap/Container';
import Card from 'react-bootstrap/Card';

const product = {
  name: 'jolof rice',
  desc: 'nigeria jolof',
  price: 1200,
  _id: 'kzd',
  kitchenName: 'iyah-dolof kitchen',
  kitchenRating: 3.5,
  verified: false,
  physicalAddress: '30 udeji street lagos island',
  status: 'available',
  content: ['2 portions of rice', '1 chicken lap'],
  imgs: [
    '/images/ghanajolof.jpg',
    '/images/giudau.webp',
    '/images/fdjfkgfgfg.jpg',
  ],
};

const cartItems = [
  {
    name: 'jolof rice',
    desc: 'nigeria jolof',
    price: 1200,
    _id: 'kzd',
    kitchenName: 'iyah-dolof kitchen',
    kitchenRating: 3.5,
    verified: false,
    physicalAddress: '30 udeji street lagos island',
    status: 'available',
    content: ['2 portions of rice', '1 chicken lap'],
    imgs: [
      '/images/ghanajolof.jpg',
      '/images/giudau.webp',
      '/images/fdjfkgfgfg.jpg',
    ],
  },
  {
    name: 'jolof rice',
    desc: 'nigeria jolof',
    price: 1200,
    _id: 'kzd',
    kitchenName: 'iyah-dolof kitchen',
    kitchenRating: 3.5,
    verified: false,
    physicalAddress: '30 udeji street lagos island',
    status: 'available',
    content: ['2 portions of rice', '1 chicken lap'],
    imgs: [
      '/images/ghanajolof.jpg',
      '/images/giudau.webp',
      '/images/fdjfkgfgfg.jpg',
    ],
  },
  {
    name: 'jolof rice',
    desc: 'nigeria jolof',
    price: 1200,
    _id: 'kzd',
    kitchenName: 'iyah-dolof kitchen',
    kitchenRating: 3.5,
    verified: false,
    physicalAddress: '30 udeji street lagos island',
    status: 'available',
    content: ['2 portions of rice', '1 chicken lap'],
    imgs: [
      '/images/ghanajolof.jpg',
      '/images/giudau.webp',
      '/images/fdjfkgfgfg.jpg',
    ],
  },
];

function Product() {
  const params = useParams();
  const { id } = params;
  const [cartItem, setCartItem] = 

  useEffect(() => {
    const productHandler = async () => {
      try {
        const { data } = await axios.get();
      } catch (error) {
        console.log(error);
      }
    };
  }, []);

  const [currentImage, setCurrentImage] = useState(product.imgs[0]);

  return (
    <div className="" style={{ overflowX: 'hidden' }}>
      <NavSearch />
      <Container>
        <Row className="">
          <Col md={!product.verified ? 9 : 12} className="product  p-3">
            <Row>
              <Col md={9} className="rounded">
                <div style={{ height: '60vh' }}>
                  <img
                    src={currentImage}
                    alt=""
                    className="rounded "
                    style={{
                      width: '100%',
                      height: '100%',
                      objectFit: 'contain',
                    }}
                  />
                </div>
                <div className="d-flex my-2 gap-2 justify-content-center">
                  {product.imgs.map((item, index) => (
                    <Link
                      key={index}
                      style={{ maxWidth: '50px', height: '50px' }}
                    >
                      {' '}
                      <img
                        src={item}
                        alt=""
                        style={{
                          maxWidth: '50px',
                          height: '50px',
                        }}
                        onClick={() => setCurrentImage(item)}
                        className={
                          currentImage === item
                            ? 'border rounded border-success p-1'
                            : ''
                        }
                      />
                    </Link>
                  ))}
                </div>
                <div
                  style={{ width: 'fit-content' }}
                  className="bg-success d-flex flex-column my-3  p-2 text-white rounded text-capitalize bg-opacity-"
                >
                  <strong className="fs-5 fw-bold">
                    {' '}
                    Price:{' '}
                    <span className="border-bottom border-grey">
                      N{product.price.toFixed(2)}
                    </span>
                  </strong>

                  <strong
                    className="text-capitalize "
                    style={{ width: 'fit-content' }}
                  >
                    Name:{' '}
                    <span className="border-bottom border-grey">
                      {' '}
                      {product.name}
                    </span>
                  </strong>

                  <span style={{ wordBreak: 'break-word' }}>
                    <strong>Description: </strong>
                    {product.desc}
                  </span>
                  <div>
                    <strong>Content: </strong>
                    {product.content}
                  </div>
                </div>
              </Col>
              <Col md={3} className=" p-0">
                <ListGroup variant="flush">
                  <ListGroup.Item className="d-flex align-items-center gap-1">
                    <strong className="text-capitalize text-secondary">
                      {product.kitchenName}
                    </strong>
                    <img
                      src="https://cdn-icons-png.freepik.com/512/7641/7641727.png"
                      alt=""
                      style={{ width: '20px' }}
                    />
                  </ListGroup.Item>
                  <ListGroup.Item className="d-flex flex-column">
                    <span>Physical Address: </span>
                    {product.physicalAddress}
                  </ListGroup.Item>
                  <ListGroup.Item className="d-flex flex-column">
                    {' '}
                    <span>Rating :</span>
                    <div className="d-flex align-items-center ">
                      <StarOutlineIcon className="text-warning " />
                      <StarOutlineIcon className="text-warning " />
                      <StarOutlineIcon className="text-warning " />
                      <StarOutlineIcon className="text-warning " />
                      <StarOutlineIcon className="text-warning" />
                    </div>
                  </ListGroup.Item>

                  <ListGroup.Item>
                    <span className="text-capitalize">
                      {product.status === 'available'
                        ? 'online'
                        : 'last seen:  5 minutes ago'}
                    </span>
                    <div className="rounded  d-flex flex-column">
                      <span className="mb-1 ">
                        {' '}
                        {product.verified
                          ? 'this user is verified'
                          : 'this user is not verified'}
                      </span>
                      <Button
                        onClick={()=>localStorage.setItem('cartItems', JSON.stringify(product))}
                        className="fw-bold"
                        variant="success"
                      >
                        Add To Tray
                      </Button>
                    </div>
                  </ListGroup.Item>
                </ListGroup>
              </Col>
            </Row>
          </Col>
          {!product.verified && (
            <Col md={3} className="">
              <Card>
                <Card.Body>
                  <strong>Total(3 Items): N157000.00</strong>
                </Card.Body>
              </Card>
              <ListGroup>
                <ListGroup.Item>{}</ListGroup.Item>
              </ListGroup>
            </Col>
          )}
        </Row>
      </Container>
    </div>
  );
}

export default Product;
