import React, { useState } from 'react';
import Form from 'react-bootstrap/Form';
import InputGroup from 'react-bootstrap/InputGroup';
import Button from 'react-bootstrap/Button';
import SearchOutlinedIcon from '@mui/icons-material/SearchOutlined';

function SearchBar(props) {
  const { handleSearch, setQuery , query} = props;
  return (
    <div style={{ width: '100%' }}>
      <div className="search-bar d-none d-md-flex justify-content-center">
        <input
          className="border input1 border-secondary p-3"
          type="text"
          placeholder="5 st, ikeja nigeria"
          onChange={(e) => setQuery(e.target.value )}
        />
        <Button onClick={handleSearch} >Search</Button>
      </div>
      <div className="d-md-none search-bar  d-flex justify-content-center">
        <InputGroup
          className="border input2 border-secondary p-3"
          type="text"
          onChange={(e) => setQuery(e.target.value )}
          placeholder="5  st, ikeja nigeria"
          
        />
        <InputGroup className="mb-3">
        
        <Form.Control
          placeholder="Username"
          aria-label="Username"
          aria-describedby="basic-addon1"
        /><InputGroup.Text id="basic-addon1">@</InputGroup.Text>
      </InputGroup>
      </div>
    </div>
  );
}

export default SearchBar;
