import React, { useEffect, useState } from 'react';
import Container from 'react-bootstrap/esm/Container';
import { Link, useParams } from 'react-router-dom';
import Masonry, { ResponsiveMasonry } from 'react-responsive-masonry';
import './mybite.css';
import Navbar from '../navSection/Navbar';

import Footer from '../footerSection/Footer';
import SearchedItems from './SearchedItems';
import SearchBar from '../component/SearchBar';
import axios from 'axios';
import { useDispatch, useSelector } from 'react-redux';
import { fetchSuccess } from '../redux/productsSearch';

function SearchScreen() {
  const { searchedProducts, searchedQuery } = useSelector(
    (state) => state.search
  );

  const [query, setQuery] = useState( '');
  const dispatch = useDispatch();

  const handleSearch = async () => {
    try {
      const { data } = await axios.get(
        `http://localhost:5000/api/users?query=${query}`
      );
      dispatch(fetchSuccess(data));
    } catch (error) {
      console.log(error);
    }
  };

  return (
    <div className="">
      <Navbar />
      <SearchBar
        query={query}
        handleSearch={handleSearch}
        setQuery={setQuery}
      />

      <div className="search-results">
        <p className="fw-bold">
          {searchedProducts?.length} kitchens selling <span className=''>{searchedQuery}</span> near you found
        </p>
      </div>
      <Container>
        <ResponsiveMasonry columnsCountBreakPoints={{ 350: 1, 750: 2, 900: 2 }}>
          <Masonry>
            {searchedProducts?.map((item, index) => (
              <Link
                className="text-decoration-none listings border-bottom  p-2"
                to=""
                key={`${index}-${item._id}`}
              >
                {' '}
                <SearchedItems item={item} />
              </Link>
            ))}
          </Masonry>
        </ResponsiveMasonry>
      </Container>

      <div className="navigation-button">
        <button className="button1">see more</button>
      </div>
      <Footer />
    </div>
  );
}

export default SearchScreen; // Export the component
