export const userSlice = createSlice({
  name: 'userInfo',

  initialState: {
    userInfo: {},
    loading: false,
    error: null,
  },
  reducers: {
  
    fetchSuccess: (state, action) => {
      state.userInfo = action.payload;
    
    },

    clearUserInfo: ()=> init
    
  },
});

export const {fetchFail, fetchStart, fetchSuccess} = userSlice.actions

export default userSlice.reducer
