import React, { useState } from 'react';
import Form from 'react-bootstrap/Form';
import InputGroup from 'react-bootstrap/InputGroup';
import Button from 'react-bootstrap/Button';
import SearchOutlinedIcon from '@mui/icons-material/SearchOutlined';
import { useLocation } from 'react-router-dom';

function SearchBar(props) {
  const { handleSearch, setQuery, id, setPrice, setCategory } = props;
  const location = useLocation()
  
  
  return (
    <div style={{ width: '100%' }}>
      <div  className="search-bar d-none d-md-flex justify-content-center">
      <InputGroup className="mb-3 searchba" style={{ width: '80%' }}>
      {location.pathname.includes(`/kitchen/${id}`) ?  
          <Form.Control
            placeholder="write your address here!"
            aria-describedby="search"
            id="search"
            className="border border-success"
          onChange={(e)=>{
              setQuery(e.target.value);
              setPrice(e.target.value);
              setCategory(e.target.value);
           
            }}
          />
        : <Form.Control
        placeholder="write your address here!"
        aria-describedby="search"
        id="search"
        className="border border-success"
      onChange={(e)=>{
          setQuery(e.target.value);}
        
       
        }
      /> }
          <Button 
          onClick={handleSearch}
            variant="success"
            id="basic-addon1"
            className=" text-white fw-bold d-flex align-items-center"
          >
            <SearchOutlinedIcon />
            Search
          </Button>
        </InputGroup>
      </div>
      <div className="d-md-none search-bar  d-flex justify-content-center">
        {/* <InputGroup
          className="border input2 border-secondary p-3"
          type="text"
          onChange={(e) => setQuery(e.target.value)}
          placeholder="5  st, ikeja nigeria"
        /> */}
        {/* <InputGroup className="mb-3 " style={{width:"100%", }}>
          
          <InputGroup.Text className='' style={{width:"100%",  height:"35px"}}>
            <div className='border w-100 d-flex'>
          <Form.Control
          style={{ height:"35px"}}
          className='  p-3'
          placeholder="5  st, ikeja nigeria"
            
            type="text"
            onChange={(e) => setQuery(e.target.value)}
            
          />
            <Button style={{ height:"35px"}}  onClick={handleSearch}>Search</Button>
            </div>
          </InputGroup.Text>

        </InputGroup> */}

        <InputGroup className="mb-3 searchba">
          <Form.Control
            placeholder="write your address here!"
            aria-describedby="search"
            id="search"
            className="border border-success"
            onChange={(e) => setQuery(e.target.value)}
           
          />
          <Button 
          onClick={handleSearch}
            variant="success"
            id="basic-addon1"
            className=" text-white fw-bold d-flex align-items-center"
          >
            <SearchOutlinedIcon />
            Search
          </Button>
        </InputGroup>
      </div>
    </div>
  );
}

export default SearchBar;
