import React, { useEffect, useState } from 'react';
import Container from 'react-bootstrap/Container';
import MenuIcon from '@mui/icons-material/Menu';
import CircleNotificationsIcon from '@mui/icons-material/CircleNotifications';
import AccountCircleIcon from '@mui/icons-material/AccountCircle';

function Navbar() {
  const [prevScrollPos, setPrevScrollPos] = useState(0);
  const [visible, setVisible] = useState(true);

  const handleScroll = () => {
    const currentScrollPos = window.pageYOffset;
    setVisible(prevScrollPos > currentScrollPos || currentScrollPos < 10);
    setPrevScrollPos(currentScrollPos);
  };

  useEffect(() => {
    window.addEventListener('scroll', handleScroll);
    return () => {
      window.removeEventListener('scroll', handleScroll);
    };
  }, [prevScrollPos, visible, hand]);
  return (
    <div className={`bg-success  ${visible ? 'visible' : 'hidden'}`}>
      <Container className="text-white py-1 d-flex justify-content-between align-items-center">
        <div className="d-flex gap-3 align-items-center navbarIcon px-2">
          <MenuIcon className="fs-2 fw-bold" />
          <strong className="fw-bold fs-5">M-bite</strong>
        </div>
        <div className="d-flex align-items-center gap-3">
          <CircleNotificationsIcon className="fs-2 fw-bold" />
          <div className="d-flex gap-1 align-items-center">
            <AccountCircleIcon className="fs-2 fw-bold" />
            <span className="d-none d-md-flex fw-bold text-capitalize">joseph oke...</span>
          </div>
        </div>
      </Container>
    </div>
  );
}

export default Navbar;
