import React, { useEffect, useState } from 'react';
import Container from 'react-bootstrap/esm/Container';
import { Link, useParams } from 'react-router-dom';
import Masonry, { ResponsiveMasonry } from 'react-responsive-masonry';
import './mybite.css';
import Navbar from '../navSection/Navbar';

import Footer from '../footerSection/Footer';
import SearchedItems from './SearchedItems';
import SearchBar from '../component/SearchBar';
import axios from 'axios';

const kitchens = [
  {
    name: 'WHO GO CHOP RESTAURANTS',
    timeOpen: 'Open from Mon - Sun 7:00am - 11:00pm',
    address: '7 olodi street apapa lagos',
    km: 17,
    status: 'Available',
    img: '/images/freshfoods.jpg',
  },
  {
    name: 'ADOF BUGGERS',
    timeOpen: 'Open from Mon - Sun 6:00am - 11:00pm',
    address: '20 kudirat abiola way oregun ikeja lagos',
    km: 65,
    status: 'Not available',
    img: '/images/bugerNew.jpg',
  },
  {
    name: 'LOAXTIZER FOODs',
    timeOpen: 'Open from Mon - Sun 11:00am - 12:00pm',
    address: 'oluwaleyimu street off allen avenue ikeja lagos',
    km: 5,
    status: ' available',
    img: '/images/jolofffff.jpg',
  },
];

function SearchScreen(props) {
  
 console.log(carouselData)

 const{ query}=useParams()
 const [setQuery]=useState('')
  
  useEffect(() => {
    const handleSearch=async()=>{
      try {
        const {data} = await axios.get(
          `http://localhost:5000/api/users?query=${query}`
        );
        console.log(data);
      } catch (error) {
        console.log(error);
      }
    }
    handleSearch()
  },[query]);

  return (
    <div className="">
      <Navbar />
      <SearchBar query={query} setQuery={setQuery} />

      <div className="search-results">
        <p className="fw-bold">{kitchens?.length} kitchens near you found</p>
      </div>
      <Container>
        <ResponsiveMasonry columnsCountBreakPoints={{ 350: 1, 750: 2, 900: 2 }}>
          <Masonry>
            {kitchens?.map((item, index) => (
              <Link
                className="text-decoration-none listings border-bottom  p-2"
                to=""
                key={index}
              >
                {' '}
                <SearchedItems item={item} />
              </Link>
            ))}
          </Masonry>
        </ResponsiveMasonry>
      </Container>

      <div className="navigation-button">
        <button className="button1">see more</button>
      </div>
      <Footer />
    </div>
  );
}

export default SearchScreen; // Export the component
