import React, { useState } from 'react';
import Navbar from '../navSection/Navbar';
import SearchBar from '../component/SearchBar';
import SearchOutlinedIcon from '@mui/icons-material/SearchOutlined';
import Button from 'react-bootstrap/Button';
import KeyboardArrowRightIcon from '@mui/icons-material/KeyboardArrowRight';
import KeyboardArrowLeftIcon from '@mui/icons-material/KeyboardArrowLeft';
import './navbar.css';
import Form from 'react-bootstrap/Form';
import InputGroup from 'react-bootstrap/InputGroup';

function NavSearch() {
  const [searchOpen, setSearchOpen] = useState(false);
  const [query, setQuery] = useState('');

  const handleSearchOpen = () => {
    setSearchOpen(!searchOpen);
  };

  const handleQuery = () => {
    navigate(query ? `/search/${query}` : `/search`)
  }
  return (
    <div className="Navsearchparent">
      <Navbar />
      {!searchOpen && (
        <div className="d-md-none searchOpenIcon" onClick={handleSearchOpen}>
          {' '}
          <Button variant="success" className="bg-success d-flex border btn1">
            <SearchOutlinedIcon className="text-white" />{' '}
            <KeyboardArrowRightIcon />
          </Button>
        </div>
      )}
      <div className="my- m-auto d-none d-md-block mdSearchBar">
      <div style={{width:"100%", }}>
      <Form onSubmit={handleQuery}>
        <InputGroup className="mb-3 ">
          <Form.Control
            placeholder="Search for eateries near you!"
            aria-describedby="search"
            id="search"
            className="border border-success"
            onChange={(e) => setQuery(e.target.value)}
            autoComplete="new-password"
          />
          <Button
            variant="success"
            id="basic-addon1"
            className=" text-white fw-bold d-flex align-items-center"
            type="submit"
          >
            <SearchOutlinedIcon />
            Search
          </Button>
        </InputGroup>
      </Form>
    </div>
      </div>
      {searchOpen && (
        <div className={'d-flex bg-success  d-md-none mobileSearchPosition'}>
          <SearchBar />
          <Button variant="success" className="bg-success btn1">
            <KeyboardArrowLeftIcon
              onClick={handleSearchOpen}
              className="text-white border"
            />
          </Button>
        </div>
      )}
    </div>
  );
}

export default NavSearch;
