import React, { useEffect, useState } from 'react';
import Button from 'react-bootstrap/Button';
import Navbar from '../navSection/Navbar';
import Form from 'react-bootstrap/Form';
import InputGroup from 'react-bootstrap/InputGroup';
import { Link, useNavigate } from 'react-router-dom';
import Footer from '../footerSection/Footer';
import axios from 'axios';
import HttpsIcon from '@mui/icons-material/Https';
import { useDispatch, useSelector } from 'react-redux';
import { fetchSuccess } from '../redux/userSlice';
import Error from '../utils/Error';
import { toast } from 'react-toastify';
import { api } from '../utils/apiConfig';

function SignUp() {
  const [name, setName] = useState('');
  const [surname, setSurname] = useState('');
  const [firstName, setFirstName] = useState('');
  const [comfirmPassword, setComfirmPassword] = useState('');
  const [country, setCountry] = useState('');
  const [email, setEmail] = useState('');
  const [state, setState] = useState('');
  const [age, setAge] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState(null);
  const dispatch = useDispatch();
  const navigate = useNavigate();
  const { userInfo } = useSelector((state) => state.user);

  const handleSignUp = async (e) => {
    e.preventDefault();
    if (comfirmPassword.toString() === password.toString()) {
      try {
        const { data } = await axios.post(`${api}/api/users/register`, {
          name: name,
          surname: surname,
          firstname: firstName,
          password: password,
          age: age,
          country: country,
          state: state,
          email: email,
        });
        dispatch(fetchSuccess(data));
      } catch (error) {
        setError(error.response.data.message);
      }
    } else {
      toast.error(error.response.data.message, {
        autoClose: true,
        theme: 'colored',
        toastId: 'unique-toast-id',
      });
    }
  };

  useEffect(() => {
    if (userInfo?.user?.name) {
      navigate('/');
    }
  });

  return (
    <div
      style={{ width: '', backgroundColor: 'lightgrey', overflowX: 'hidden' }}
    >
      <Navbar />

      <div className="d-flex justify-content-center py-5 flex-column align-items-center">
        <div>
          
        </div>
       
      </div>
      <Footer />
    </div>
  );
}

export default SignUp;
