import React, { useState } from 'react';
import Button from 'react-bootstrap/Button';
import Navbar from '../navSection/Navbar';
import Form from 'react-bootstrap/Form';
import InputGroup from 'react-bootstrap/InputGroup';
import { Link } from 'react-router-dom';
import Footer from '../footerSection/Footer';
import axios from 'axios';
import { fetchFail, fetchStart, fetchSuccess } from '../redux/userSlice';

function SignIn() {
  const [name, setName] = useState('');
  const [password, setPassword] = useState('');
  const dispatch = use

  const handleLogin =async (e) => {
    e.preventDefault();
    dispatch(fetchStart())
    try {
      
      const { data } = await axios.post(`http://localhost:5000/api/users/signin`, {
        name: name,
        password: password,
      });
      dispatch(fetchSuccess(data))
      console.log('Login successful', data);
    } catch (error) {
      console.log(error);
      dispatch(fetchFail(error.message))
    }
  };

  return (
    <div>
      <Navbar />

      <div
        className="d-flex justify-content-center flex-column align-items-center"
        style={{ width: '100%', height: '70vh' }}
      >
        <Button
          variant="danger"
          className="bg-danger opacity-75 p-1"
          style={{ borderRadius: '50%' }}
        >
          key
        </Button>
        <strong>Sign In</strong>
        <Form onSubmit={handleLogin}>
          <InputGroup className="my-3">
            <InputGroup.Text>Name</InputGroup.Text>
            <Form.Control
              type="text"
              onChange={(e) => setName(e.target.value)}
            />
          </InputGroup>
          <InputGroup>
            <InputGroup.Text>Password</InputGroup.Text>
            <Form.Control
              type="text"
              onChange={(e) => setPassword(e.target.value)}
            />
          </InputGroup>
          <div className="d-grid my-3">
            <Button variant="success" className="bg-success" type="submit">
              Login
            </Button>
          </div>
        </Form>
        <div>
          <strong>
            {' '}
            New customer ? <Link> sign up here</Link>
          </strong>
        </div>
      </div>
      <Footer />
    </div>
  );
}

export default SignIn;
