import React from 'react';
import { Link } from 'react-router-dom';
import Cards from '../component/Cards';
import { Box, Skeleton } from '@mui/material';

function Recommended(props) {
  const { availableProduct, loading } = props;
  return (
    <div>
      {availableProduct?.length > 0 && (
        <span className="font1bg fw-bold p-1 d-flex justify-content-center align-items-center">
          Recommended Items from the store in your cart
        </span>
      )}
      <div
        className="d-flex gap-3 justify-content-cente align-items-center my-5 "
        style={{ overflowX: 'hidden', position: 'relative' }}
      >
        {(loading ? Array.from(new Array(5)) : availableProduct).map(
          (item, index) => (
            <Link
              to={`/product/${item?._id}`}
              key={`${index}`}
              className="box text-decoration-none "
              style={{
                height: '270px',
                minWidth: '200px',
              }}
            >
              {' '}
              {item ? (
                <Cards loading={loading} item={item} />
              ) : (
                <div>
                  <Skeleton variant="rectangular" width={210} height={118} />
                  <Box sx={{ pt: 0.5 }}>
                    <Skeleton />
                    <Skeleton width="60%" />
                  </Box>
                </div>
              )}
            </Link>
          )
        )}
      </div>
    </div>
  );
}

export default Recommended;
