import { createSlice } from '@reduxjs/toolkit';

export const productsSearch = createSlice({
  name: 'searchedProducts',

  initialState: {
    searchedProducts: [],
    searchedQuery: '',
  },
  reducers: {
    fetchCart: (state, action) => {
      const newItem = action.payload;
      const existItem = state.searchedProducts?.map(
        (item) => item._id === newItem._id
      );
      if (existItem) {
        const cartItems = state.searchedProducts?.map((item) =>
          item._id === existItem._id ? item : newItem
        );
        return (state.searchedProducts = cartItems);
      }
     
    },

    searchedString: (state, action) => {
      state.searchedQuery = action.payload;
    },
  },
});

export const { fetchCart, searchedString } = productsSearch.actions;

export default productsSearch.reducer;
