import { createSlice } from '@reduxjs/toolkit';

export const productsSearch = createSlice({
  name: 'searchedProducts',
 
  initialState: {
    cartItems: [],
    searchedQuery:'',
  },
  reducers: {
    fetchSuccess: (state, action) => {
      const newItem = action.payload
     const allCarts = state.cartItems.find((item)=>item._id === newItem)
     if(!allCarts){
        state.cartItems.push(newItem)
     }else{
      state.cartItems = allCarts.quantity +1
     }
    },




    searchedString:(state, action) => {
      state.searchedQuery=action.payload;
  }
  },
});

export const { fetchSuccess , searchedString} =
  productsSearch.actions;

export default productsSearch.reducer;
