import React from 'react';
import Card from 'react-bootstrap/Card';

function Cards({ item }) {
  return (
    <div className="" style={{minHeight:"100%", maxHeight:"100%"}} >
      <Card style={{minHeight:"100%", maxHeight:"100%"}}>
        <img src={item.imgs[0]} alt="" style={{minHeight:"60%", }} />
        <Card.Body style={{height:"40%"}}>
        <div className='d-flex align-items-center flex-column justify-content-center'>
        <span className='fw-bold text-capitalize text-secondary'>{item.name.length > 15 ? `${item.name.slice(0,15)}...` : item.name}</span>
        <span className='text-secondary'>
          {item.desc.length > 15 ? `${item.desc.slice(0,15)}...` : item.desc}
        </span >
        <strong className=' text-success' class>N{item.price.toFixed(2)}</strong>
      </div>
        </Card.Body>
      </Card>
     
    </div>
  );
}

export default Cards;
