import React from 'react';
import ListGroup from 'react-bootstrap/ListGroup';

function AcctHistory(props) {
  const { history, userInfo } = props;
  return (
    <ListGroup>
      <strong>History</strong>
      {history &&
        history.length > 0 &&
        history.map((item, index) => (
          <ListGroup.Item style={{width:"fit-content"}}>
            <span
              key={index}
              className={
                item.buyerId === userInfo?.user._id
                  ? `text-danger fw-bold`
                  : `fw-bold text-success`
              }
            >
              {item.buyerId === userInfo?.user._id
                ? `-N${item?.amount.toFixed(2)}`
                : `+N${item?.amount.toFixed(2)}`}{' '}
              {item.buyerId === userInfo.user?._id
                ? `sent to ${item.businessName}`
                : `Order from ${item.buyerName}`}
            </span>
          </ListGroup.Item>
        ))}
    </ListGroup>
  );
}

export default AcctHistory;
