import React, { useEffect, useState } from 'react';
import Container from 'react-bootstrap/esm/Container';
import { Link, useLocation } from 'react-router-dom';
import Masonry, { ResponsiveMasonry } from 'react-responsive-masonry';
import './mybite.css';
import Navbar from '../navSection/Navbar';
import Footer from '../footerSection/Footer';
import SearchedItems from './SearchedItems';
import SearchBar from '../component/SearchBar';
import axios from 'axios';
import { api } from '../utils/apiConfig';
import Button from 'react-bootstrap/esm/Button';

function SearchScreen() {

  const { search } = useLocation();
  const sp = new URLSearchParams(search);
  const query = sp.get('query') || 'all';
  const page = parseInt(sp.get('page')) || 1; // Parse page as integer

  const [products, setProducts] = useState([]);
  const [currentPage, setCurrentPage] = useState(page);
  const [pageData, setPageData] = useState({}); // Store page data in an object

  useEffect(() => {
    const fetchData = async () => {
      try {
        // Fetch data for the current page if it's not already fetched
        if (!pageData[currentPage]) {
          const { data } = await axios.get(
            `${api}/api/users?query=${query}&page=${currentPage}`
          );
          setPageData(prevPageData => ({
            ...prevPageData,
            [currentPage]: data // Store the fetched data in the pageData object
          }));
        }
        // Set products based on the data for the current page
        setProducts(pageData[currentPage]);
      } catch (error) {
        console.log(error);
      }
    };

    fetchData();
  }, [query, currentPage]);

  const handlePageChange = () => {
    // Update currentPage state to move to the next page
    setCurrentPage(currentPage + 1);
  };


  return (
    <div className="">
      <Navbar />
      <SearchBar />

      <div className="search-results">
        <p className="fw-bold">
          {products?.length} kitchens selling{' '}
          <span className="border-bottom border-primary text-danger">
            {query !== 'all' ? query : ''}
          </span>{' '}
          near you found
        </p>
      </div>
      <Container>
        <ResponsiveMasonry columnsCountBreakPoints={{ 350: 1, 750: 2, 900: 2 }}>
          <Masonry>
            {products?.map((item, index) => (
              <Link
                to={`/kitchen/${item._id}`}
                className="text-decoration-none listings border-bottom  p-2"
                key={`${index}-${item._id}`}
              >
                {' '}
                <SearchedItems item={item} />
              </Link>
            ))}
          </Masonry>
        </ResponsiveMasonry>
      </Container>

      <div className="navigation-button">
        <Button  onClick={ handlePageChange} className="button1">
          see more
        </Button>
      </div>
      <Footer />
    </div>
  );
}

export default SearchScreen; // Export the component
