import React, { useEffect, useState } from 'react';

import NavSearch from '../navSection/NavSearch';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import { Link, useParams } from 'react-router-dom';
import axios from 'axios';

import './product.css';
import Button from 'react-bootstrap/Button';
import Container from 'react-bootstrap/Container';
import Card from 'react-bootstrap/Card';

import Footer from '../footerSection/Footer';
import ProductDetails from './ProductDetails';
import ExpandLessIcon from '@mui/icons-material/ExpandLess';
import CartCard from '../cartSection/CartCard';

import { useSelector } from 'react-redux';
import { api } from '../utils/apiConfig';





function Product() {
  const params = useParams();
  const { id } = params;
  const [product, setProduct] = useState({});
  const [loading, setLoading] = useState(false);
  const [isSmallScreen, setIsSmallScreen] = useState(false);
  const {cartItems} = useSelector((state)=> state.cart)

  useEffect(() => {
    const productHandler = async () => {
      setLoading(true);
      try {
        const { data } = await axios.get(
          `${api}/api/products/find/${id}`
        );
        setProduct(data);
        setLoading(false);
      } catch (error) {
        console.log(error);
        setLoading(false);
      }
    };
    productHandler();
  }, [id]);

  
 

  useEffect(() => {
    const checkScreenSize = () => {
      setIsSmallScreen(window.innerWidth < 1200);
    };
    checkScreenSize();

    window.addEventListener('resize', checkScreenSize);

    return () => {
      window.removeEventListener('resize', checkScreenSize);
    };
  }, []);

  

  return (
    <div className="" style={{ overflowX: 'hidden' }}>
      <NavSearch />
      <div
        style={{
          height: isSmallScreen ? '89vh' : 'auto',
          overflowY: isSmallScreen ? 'scroll' : 'visible',
        }}
      >
        <Container>
          <Row className="">
            <Col
              md={isSmallScreen ? 12 : cartItems.length > 0 ? 9 : 12}
              className="product  p-3"
            >
              {product?.imgs && <ProductDetails loading={loading} product={product} />}
            </Col>
            {!isSmallScreen && cartItems.length > 0 && (
              <Col md={3} className="my-3">
                <CartCard product={product._id} />

                <Link to="/cart" className="d-grid my-2 text-decoration-none">
                  <Button
                    to="/cart"
                    variant="light"
                    className="text-success border-secondary "
                  >
                    See All
                  </Button>
                </Link>
              </Col>
            )}
          </Row>
        </Container>
        <Footer />
      </div>

      {isSmallScreen && cartItems.length > 0 && (
        <Card >
          <Link
            to="/cart"
            className="p-2 d-flex align-items-center  justify-content-between text-decoration-none"
          >
            <strong className="text-dark">Cart(3 Items)</strong>
            <div className="d-flex align-items-center text-success">
              {' '}
              <strong>Total: {`N${cartItems.reduce((a,c)=> a + c.price * c.quantity, 0).toFixed(2)}`} </strong>
              <ExpandLessIcon className="fs-1 fw-bold" />
            </div>
          </Link>
        </Card>
      )}
    </div>
  );
}

export default Product;
