import { createSlice } from '@reduxjs/toolkit';

export const productsSearch = createSlice({
  name: 'searchedProducts',

  initialState: {
    searchedProducts: [],
    searchedQuery: '',
  },
  reducers: {
    fetchCart: (state, action) => {
      const newItem = action.payload;
      state.searchedProducts = newItem
     
    },

    searchedString: (state, action) => {
      state.searchedQuery = action.payload;
    },
  },
});

export const { fetchCart, searchedString } = productsSearch.actions;

export default productsSearch.reducer;
