import React, { useEffect, useState } from 'react';
import Form from 'react-bootstrap/Form';
import InputGroup from 'react-bootstrap/InputGroup';
import SearchOutlinedIcon from '@mui/icons-material/SearchOutlined';
import Button from 'react-bootstrap/Button';

const Map = () => {
  const [query, setQuery] = useState('');
  useEffect(() =>{
    if(query){
      navigate(query ? `/search?query=${query}` : '/search')
    }
  })
  useEffect(() => {
    if ('geolocation' in navigator) {
      navigator.geolocation.getCurrentPosition(
        (position) => {
          const { latitude, longitude } = position.coords;

          const mapUrl = `https://www.google.com/maps/embed?pb=!1m14!1m12!1m3!1d126844.06348606381!2d${longitude}!3d${latitude}!2m3!1f0!2f0!3f0!3m2!1i1024!2i768!4f13.1!5e0!3m2!1sen!2sng!4v1706517127493!5m2!1sen!2sng`;

          document.getElementById('map-iframe').src = mapUrl;
        },
        (error) => {
          console.error('Error getting user location:', error);
        }
      );
    } else {
      console.error('Geolocation is not supported by this browser.');
    }
  }, []);

  return (
    <div>

        <Form className="searchP">
          
      <iframe
        width="100%"
        height="300"
        title="Google Map Embed"
        id="map-iframe"
        src=""
        style={{ border: '0' }}
        allowFullScreen=""
        loading="lazy"
        referrerPolicy="no-referrer-when-downgrade"
      ></iframe>
    
   
        <InputGroup className="mb-3 searchbar">
          <Form.Control
            placeholder="write your address here!"
            aria-describedby="search"
            id="search"
            className="border border-success"
            onChange={(e) => setQuery(e.target.value)}
           
          />
          <Button
            variant="success"
            id="basic-addon1"
            className=" text-white fw-bold d-flex align-items-center"
          >
            <SearchOutlinedIcon />
            Search
          </Button>
        </InputGroup>
      </Form>
    </div>
  );
};

export default Map;
