import React, { useEffect, useState } from 'react';
import Navbar from '../navSection/Navbar';
import Container from 'react-bootstrap/Container';
import Button from 'react-bootstrap/Button';

import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import ListGroup from 'react-bootstrap/ListGroup';
import './order.css';
import OrderDetails from './OrderDetails';
import axios from 'axios';
import { useParams } from 'react-router-dom';
import { useSelector } from 'react-redux';
import Error from '../utils/Error';
import NavSearch from '../navSection/NavSearch';
import { toast } from 'react-toastify';

function Order() {
  const params = useParams();
  const { id } = params;
  const [data, setData] = useState({});
  const { userInfo } = useSelector((state) => state.user);
  const [smallScreen, setSmallScreen] = useState(false);


  useEffect(() => {
    const handleOrder = async () => {
      try {
        const { data } = await axios.get(
          `http://localhost:5000/api/orders/find/${userInfo.user?._id}/${id}`
        );
        setData(data);
      } catch (error) {

        toast.error(error.response.data.message, {autoClose:false, theme: "colored", toastId:"unique-toast-id"});
      }
    };
    handleOrder();
  }, [id, userInfo.user?._id]);

  console.log()

  

  const takeOrder = async (businessId, orderId) => {
    try {
      const res = await axios.put(
        `http://localhost:5000/api/orders/takeorder/${businessId}/${orderId}`
      );
      const isTaken = res.data;
      setData((prevData) => ({
        ...prevData,
        details: {
          ...prevData.details,
          isTaken: isTaken,
        },
      }));
    } catch (error) {
      toast.error(error.response.data.message, {autoClose:5000, theme: "colored", toastId:"unique-toast-id"});
    
    }
  };



  
  useEffect(() => {
    const smallSize = () => {
      setSmallScreen(window.innerWidth < 1200);
    };
    smallSize();
    window.addEventListener('resize', smallSize);

    return () => {
      window.removeEventListener('resize', smallSize);
    };
  }, []);

  const items = (data) => {
    return data?.products?.map((item, index) => (
      <span key={index} className="font1 ">
        {item.category === 'food'
          ? `${item.quantity} plate  of ${item.name},`
          : item.category === 'meat '
          ? ` ${item.quantity} pieces of ${item.name},`
          : `${item.quantity} pieces of ${item.name},
                                `}
      </span>
    ));
  };

  return (
    <div>
      <NavSearch/>
      <>
        {data?.products?.length > 0 &&
          <Container className="my-3">
            <div className="fw-bold fs-5 text-center text-success">
              Order Details({data?.products?.length} items)
            </div>
            <div className="d-flex  p-3 border   justify-content-between ">
              <div className="d-flex flex-column ">
                <div className="">
                  <h3 className="d-none d-md-flex">
                    Order ID: {data?.details?._id}
                  </h3>
                  <h6 className="d-md-none">Order ID: {data?.details?._id}</h6>
                </div>
                <div className="d-flex flex-wrap  flex-column text-capitalize">
                  <div className="d-flex flex-wrap  gap-2">
                    <strong>Name: </strong>
                    <span className="font">
                      {data?.details?.shippingAddress?.name}
                    </span>
                  </div>
                  <div className="d-flex  gap-2">
                    <strong>phone number: </strong>
                    <span className="font">
                      {data.details?.shippingAddress?.phoneNumber}
                    </span>
                  </div>
                  <div className="d-flex flex-wrap  gap-1">
                    <strong>Address:</strong>
                    <div>
                      {' '}
                      <span className="font">
                        {data.details?.shippingAddress?.street}{' '}
                        {data.details?.shippingAddress?.localGvt},{' '}
                        {data.details?.shippingAddress?.state}{' '}
                        {data.details?.shippingAddress?.country}
                      </span>
                    </div>
                  </div>
                </div>
                {data?.products && data?.products[0]?.businessId !== false && (
                  <>
                    {data.details && !data?.details?.isTaken ? (
                      <div className="d-flex align-items-center mt-2 gap-3">
                        <Button
                          variant="success"
                          className="fw-bold d-flex align-items-center"
                          onClick={() =>
                            takeOrder(data.details.businessId, data.details._id)
                          }
                        >
                          Accept
                        </Button>
                        <Button
                          variant="danger"
                          className="fw-bold d-flex align-items-center"
                        >
                          Refund & Cancel
                        </Button>
                      </div>
                    ) : (
                      <div className="font1bg my-3 p-2 border border-rounded">
                        <span>
                          Note: this order has been comfirmed, for futher
                          enquiries or help, kindly report below{' '}
                        </span>
                        <Button
                          variant="white"
                          className="fw-bold text-danger border-danger mt-1 d-flex align-items-center"
                        >
                          Report/dispute
                        </Button>
                      </div>
                    )}
                  </>
                )}





                {data.details &&
                  data.details.buyerId !== data.details.businessId && (
                    <div className="font1bg my-3 p-2 border border-rounded">
                      <span>
                        Note: this order has been comfirmed, for futher
                        enquiries or help, kindly report below{' '}
                      </span>
                      <Button
                        variant="white"
                        className="fw-bold text-danger border-danger mt-1 d-flex align-items-center"
                      >
                        Report/dispute
                      </Button>
                    </div>
                  )}
              </div>
            </div>
            <div>
              <div className="fs-4 fw-bold text-center my-3">Items</div>
              <Row>
                <Col md={8} className="mb-3">
                  <OrderDetails data={data} smallScreen={smallScreen} />
                </Col>
                <Col md={4}>
                  <ListGroup>
                    <strong>Total:N{data?.details?.total}</strong>
                    <ListGroup.Item className="font1bg">
                      {items(data)}
                    </ListGroup.Item>
                  </ListGroup>
                </Col>
              </Row>
            </div>
          </Container>
        }
      </>
    </div>
  );
}

export default Order;
