import React, { useEffect, useState } from 'react';

import NavSearch from '../navSection/NavSearch';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import { Link, useParams } from 'react-router-dom';
import axios from 'axios';
import Carousel from 'react-bootstrap/Carousel';

import './product.css';
import Button from 'react-bootstrap/Button';

import Card from 'react-bootstrap/Card';

import Footer from '../footerSection/Footer';
import ProductDetails from './ProductDetails';
import ExpandLessIcon from '@mui/icons-material/ExpandLess';
import CartCard from '../cartSection/CartCard';

import { useSelector } from 'react-redux';
import { api } from '../utils/apiConfig';
import Recommended from '../recommended/Recommended';
import Cards from '../component/Cards';
import { Box, Skeleton } from '@mui/material';
import Masonry, { ResponsiveMasonry } from 'react-responsive-masonry';

function Product() {
  const params = useParams();
  const { id } = params;
  const [product, setProduct] = useState({});
  const [loadings, setLoading] = useState(false);
  const [isSmallScreen, setIsSmallScreen] = useState(false);
  const { cartItems } = useSelector((state) => state.cart);
  const [availableProduct, setAvalaibleProduct] = useState([]);

  useEffect(() => {
    const productHandler = async () => {
      setLoading(true);
      try {
        const { data } = await axios.get(`${api}/api/products/find/${id}`);
        setProduct(data);
        setLoading(false);
      } catch (error) {
        console.log(error);
        setLoading(false);
      }
    };
    productHandler();
  }, [id]);

  useEffect(() => {
    const relatedProducts = async () => {
      if (product?.type) {
        const query =
          product?.type?.length > 2
            ? product?.type.slice(0, 4)
            : product?.type || cartItems?.map((item) => item.type);
        try {
          const { data } = await axios.post(
            `${api}/api/products/recommended/${product.userId}/${id}?query=${query}`
          );
          setAvalaibleProduct(data);
          console.log('related', data, 'type', product.type);
        } catch (error) {
          console.log(error);
        }
      }
    };
    relatedProducts();
  }, [cartItems, product, id]);

  useEffect(() => {
    const checkScreenSize = () => {
      setIsSmallScreen(window.innerWidth < 1200);
    };
    checkScreenSize();

    window.addEventListener('resize', checkScreenSize);

    return () => {
      window.removeEventListener('resize', checkScreenSize);
    };
  }, []);

  const [index, setIndex] = useState(0);

  const [startIndex, setStartIndex] = useState(0);
  const itemsPerPage = 5; // Change this value as needed

  const handleNext = () => {
    if (startIndex + itemsPerPage < availableProduct.length) {
      setStartIndex(startIndex + 1);
    }
  };

  const handlePrev = () => {
    if (startIndex > 0) {
      setStartIndex(startIndex - 1);
    }
  };

  return (
    <div style={{ overflowX: 'hidden' }}>
      <NavSearch />
      <div
        style={{
          height: isSmallScreen ? '85vh' : 'auto',
          overflowY: isSmallScreen ? 'scroll' : 'visible',
        }}
      >
        <div
          style={{
            width: !isSmallScreen ? '80vw' : '',
            margin: isSmallScreen ? '' : 'auto',
          }}
        >
          <Row className="">
            <Col
              md={isSmallScreen ? 12 : cartItems.length > 0 ? 9 : 12}
              className="product  p-3 "
            >
              {
                <ProductDetails
                  isSmallScreen={isSmallScreen}
                  loadings={loadings}
                  product={product}
                />
              }
            </Col>
            {!isSmallScreen && cartItems.length > 0 && (
              <Col md={3} className="my-3">
                <CartCard product={product._id} />

                <Link to="/cart" className="d-grid my-2 text-decoration-none">
                  <Button
                    to="/cart"
                    variant="light"
                    className="text-success border-secondary "
                  >
                    See All
                  </Button>
                </Link>
              </Col>
            )}
          </Row>
        </div>

        {/* {availableProduct?.length > 0 && (
          <div>
            <div className="d-flex align-items-center my-3 flex-column">
              {availableProduct && availableProduct?.length > 0 && (
                <span className="font1bg fw-bold p-1 d-flex justify-content-center align-items-center w-100">
                  Recommended similar Items from this store
                </span>
              )}
              <div
                className="d-flex gap-3 justify-content-cente align-items-center my-5 "
                style={{ overflowX: 'hidden', position: 'relative' }}
              >
                {(loadings
                  ? Array.from(new Array(5))
                  : availableProduct && availableProduct
                )?.map((item, index) => (
                  
                  <Link
                    to={`/product/${item?._id}`}
                    key={`${index}`}
                    className="box text-decoration-none "
                    style={{
                      height: '270px',
                      minWidth: '200px',
                    }}
                  >
                    {' '}
                    {item ? (
                  
                        <Cards loading={loadings} item={item} />
                    
                    ) : (
                      <div>
                        <Skeleton
                          variant="rectangular"
                          width={210}
                          height={118}
                        />
                        <Box sx={{ pt: 0.5 }}>
                          <Skeleton />
                          <Skeleton width="60%" />
                        </Box>
                      </div>
                    )}
                  </Link>
                
                ))}
              </div>
            </div>{' '}
          </div>
        )} */}

        <div>
          <div className=" my-3 ">
            {availableProduct && availableProduct.length > 0 && (
              <span className="font1bg fw-bold p-1 d-flex justify-content-center align-items-center w-100 mb-3" >
                Recommended similar Items from this store
              </span>
            )}

           
              <div className='grid-container '>
                {(loadings
                  ? Array.from(new Array(5))
                  : availableProduct && availableProduct
                )?.map((item, index) => (
                  <Link
                    to={`/product/${item?._id}`}
                    key={`${index}`}
                    className="box text-decoration-none mb- grid-item"
                   
                  >
                    {' '}
                    {item ? (
                      <Cards loading={loadings} item={item} />
                    ) : (
                      <div>
                        <Skeleton
                          variant="rectangular"
                          width={210}
                          height={118}
                        />
                        <Box sx={{ pt: 0.5 }}>
                          <Skeleton />
                          <Skeleton width="60%" />
                        </Box>
                      </div>
                    )}
                  </Link>
                ))}
              </div>
       
          </div>
        </div>
        <Footer />
      </div>



      {isSmallScreen && cartItems.length > 0 && (
        <Card>
          <Link
            to="/cart"
            className="p-2 d-flex align-items-center  justify-content-between text-decoration-none"
          >
            <strong className="text-dark">Cart(3 Items)</strong>
            <div className="d-flex align-items-center text-success">
              {' '}
              <strong>
                Total:{' '}
                {`N${cartItems
                  .reduce((a, c) => a + c.price * c.quantity, 0)
                  .toFixed(2)}`}{' '}
              </strong>
              <ExpandLessIcon className="fs-1 fw-bold" />
            </div>
          </Link>
        </Card>
      )}
    </div>
  );
}

export default Product;
