import React, { useEffect, useState } from 'react';

import NavSearch from '../navSection/NavSearch';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import { Link, useParams } from 'react-router-dom';
import axios from 'axios';
import ListGroup from 'react-bootstrap/ListGroup';
import StarOutlineIcon from '@mui/icons-material/StarOutline';

import './product.css';
import Button from 'react-bootstrap/Button';
import Container from 'react-bootstrap/Container';
import Card from 'react-bootstrap/Card';
import Cards from '../component/Cards';
import DeleteForeverOutlinedIcon from '@mui/icons-material/DeleteForeverOutlined';
import HighlightOffOutlinedIcon from '@mui/icons-material/HighlightOffOutlined';
import Footer from '../footerSection/Footer';
import ProductDetails from './ProductDetails';

const product = {
  name: 'jolof rice',
  desc: 'nigeria jolof',
  price: 1200,
  _id: 'kzd',
  kitchenName: 'iyah-dolof kitchen',
  kitchenRating: 2.5,
  verified: false,
  physicalAddress: '30 udeji street lagos island',
  status: 'available',
  content: ['2 portions of rice', '1 chicken lap'],
  imgs: [
    '/images/ghanajolof.jpg',
    '/images/giudau.webp',
    '/images/fdjfkgfgfg.jpg',
  ],
};

const cartItems = [
  {
    name: 'jolof rice',
    desc: 'nigeria jolof',
    price: 1200,
    _id: 'kzd',
    kitchenName: 'iyah-dolof kitchen',
    kitchenRating: 3.5,
    verified: false,
    physicalAddress: '30 udeji street lagos island',
    status: 'available',
    content: ['2 portions of rice', '1 chicken lap'],
    imgs: [
      '/images/ghanajolof.jpg',
      '/images/giudau.webp',
      '/images/fdjfkgfgfg.jpg',
    ],
  },
  {
    name: 'jolof rice',
    desc: 'nigeria jolof',
    price: 1200,
    _id: 'kzd',
    kitchenName: 'iyah-dolof kitchen',
    kitchenRating: 3.5,
    verified: false,
    physicalAddress: '30 udeji street lagos island',
    status: 'available',
    content: ['2 portions of rice', '1 chicken lap'],
    imgs: [
      '/images/ghanajolof.jpg',
      '/images/giudau.webp',
      '/images/fdjfkgfgfg.jpg',
    ],
  },
  {
    name: 'jolof rice',
    desc: 'nigeria jolof',
    price: 1200,
    _id: 'kzd',
    kitchenName: 'iyah-dolof kitchen',
    kitchenRating: 3.5,
    verified: false,
    physicalAddress: '30 udeji street lagos island',
    status: 'available',
    content: ['2 portions of rice', '1 chicken lap'],
    imgs: [
      '/images/ghanajolof.jpg',
      '/images/giudau.webp',
      '/images/fdjfkgfgfg.jpg',
    ],
  },
];

function Product() {
  const params = useParams();
  const { id } = params;

  useEffect(() => {
    const productHandler = async () => {
      try {
        const { data } = await axios.get();
      } catch (error) {
        console.log(error);
      }
    };
  }, []);

  const [isSmallScreen, setIsSmallScreen] = useState(false);

  useEffect(() => {
    const checkScreenSize = () => {
      setIsSmallScreen(window.innerWidth < 1200);
    };
    checkScreenSize();

    window.addEventListener('resize', checkScreenSize);

    return () => {
      window.removeEventListener('resize', checkScreenSize);
    };
  }, []);

  return (
    <div className="" style={{ overflowX: 'hidden' }}>
      <NavSearch />
      <Container>
        <Row className="">
          <Col md={isSmallScreen ? 12 : !product.verified  ? 9 : 12} className="product  p-3" style={{ height: isSmallScreen ? '90vh' : 'auto', overflowY: isSmallScreen ? 'scroll' : 'visible' }}>
            <ProductDetails product={product} />
          </Col>
          {!product.verified && (
            <Col md={3} className="my-3">
              <Card>
                <Card.Body>
                  <strong>Total(3 Items): N157000.00</strong>
                </Card.Body>
              </Card>
              <ListGroup className="mt-2 border">
                <span className="text-center fw-bold border-bottom border-dark fs-5">
                  Your Cart Items
                </span>
                {cartItems.slice(0, 3).map((item) => (
                  <ListGroup.Item
                    key={item._id}
                    style={{ position: 'relative' }}
                  >
                    <Row>
                      <Col md={7}>
                        {' '}
                        <img
                          src={item.imgs[0]}
                          alt=""
                          style={{ width: '100%' }}
                        />
                      </Col>
                      <Col
                        md={5}
                        className="d-flex justify-content-center align-items-center flex-column "
                      >
                        <span className="fw-bold text-capitalize text-secondary">
                          {item.name}
                        </span>

                        <strong className=" text-success" class>
                          N{item.price.toFixed(2)}
                        </strong>
                        <div className="d-flex align-items-center gap-1">
                          {' '}
                          <span
                            style={{
                              width: '30px',
                              height: '30px',
                              borderRadius: '50%',
                            }}
                            className="fw-bold bg-success d-flex justify-content-center text-white"
                          >
                            -
                          </span>{' '}
                          6{' '}
                          <span
                            style={{
                              width: '30px',
                              height: '30px',
                              borderRadius: '50%',
                            }}
                            className="fw-bold bg-success d-flex justify-content-center text-white"
                          >
                            +
                          </span>{' '}
                        </div>
                      </Col>
                    </Row>
                    <span
                      style={{
                        position: 'absolute',
                        top: 1,
                        right: 2,
                        cursor: 'pointer',
                      }}
                      className="text-danger opacity-25 "
                    >
                      <HighlightOffOutlinedIcon />
                    </span>
                  </ListGroup.Item>
                ))}
                {/* <ListGroup.Item>
                  <span>Cart Items</span>
                </ListGroup.Item> */}
              </ListGroup>
            </Col>
          )}
        </Row>
      </Container>
      <Footer />
    </div>
  );
}

export default Product;
